INSERT INTO `news_contents` (`id`, `news_category_id`, `name`, `friendly_url`, `intro_text`, `content`, `image_path`, `date_created`, `page_title`, `meta_keywords`, `meta_description`) VALUES
(2, 2, 'Sáng tạo cùng Baileys', 'sang_tao_cung_baileys', 'Baileys Cà-phê là một cách bình dị và thư giãn để thưởng thức Baileys, đặc biệt vào những buổi chiều nhẹ nhàng hoặc càng ngon hơn sau bữa tối. Thực ra, Baileys  Cà-phê hợp với mọi hoạt động trong ngày của bạn. ', '&lt;h5&gt;Baileys C&amp;agrave;-ph&amp;ecirc;&lt;/h5&gt;\n&lt;p&gt;&lt;span style=&quot;color: #454545; font-family: Arial,sans-serif; font-size: 12px; line-height: 17px; text-align: justify;&quot;&gt;&lt;a href=&quot;http://muabanruoubia.com/wp-content/uploads/2010/04/bailey_2_225.jpg&quot;&gt;&lt;img class=&quot;alignleft size-full wp-image-702&quot; src=&quot;http://muabanruoubia.com/wp-content/uploads/2010/04/bailey_2_225.jpg&quot; border=&quot;0&quot; alt=&quot;bailey_2_225&quot; width=&quot;225&quot; height=&quot;150&quot; /&gt;&lt;/a&gt;Nếu ly caf&amp;eacute; của bạn thiếu ch&amp;uacute;t vị sang trọng, sao kh&amp;ocirc;ng thử &lt;a href=&quot;http://muabanruoubia.com/tag/baileys/&quot; title=&quot;Baileys&quot;&gt;Baileys&lt;/a&gt;?&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;Baileys C&amp;agrave;-ph&amp;ecirc; l&amp;agrave; một c&amp;aacute;ch b&amp;igrave;nh dị v&amp;agrave; thư gi&amp;atilde;n để thưởng thức Baileys, đặc biệt v&amp;agrave;o những buổi chiều nhẹ nh&amp;agrave;ng hoặc c&amp;agrave;ng ngon hơn sau bữa tối. Thực ra, Baileys&amp;nbsp; C&amp;agrave;-ph&amp;ecirc; hợp với mọi hoạt động trong ng&amp;agrave;y của bạn.&lt;span&gt; &lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;margin: 0px; padding: 0px; text-decoration: underline;&quot;&gt;&lt;strong&gt;C&amp;ocirc;ng thức&lt;/strong&gt;&lt;/span&gt;&lt;span&gt; &lt;/span&gt;(lượng d&amp;ugrave;ng cho một người)&lt;/p&gt;\n&lt;p&gt;Bước 1: Lấy một ly hoặc t&amp;aacute;ch d&amp;ugrave;ng để uống caf&amp;eacute;&lt;br /&gt;Bước 2: Đổ 20ml caf&amp;eacute; đen v&amp;agrave;o ly&lt;br /&gt;Bước 3: Th&amp;ecirc;m 50ml Baileys&lt;br /&gt;Bước 4: R&amp;oacute;t sữa n&amp;oacute;ng v&amp;agrave;o đầy ly&lt;br /&gt;Bước 5: Khuấy để tạo bọt ở miệng ly&lt;br /&gt;Bước 6: Rắc &amp;iacute;t s&amp;ocirc;-c&amp;ocirc;-la vụn l&amp;ecirc;n bề mặt&lt;/p&gt;\n&lt;p&gt;Thật l&amp;agrave; một hương vị c&amp;agrave; ph&amp;ecirc; cổ điển!&lt;span&gt; &lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;margin: 0px; padding: 0px; text-decoration: underline;&quot;&gt;&lt;strong&gt;Mẹo nhỏ:&lt;/strong&gt;&lt;/span&gt;&lt;br /&gt;- H&amp;atilde;y h&amp;ograve;a Baileys gần như đồng thời hoặc ngay lập tức sau khi đổ caf&amp;eacute; v&amp;agrave;o ly.&lt;/p&gt;\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\n&lt;h5&gt;Baileys Đ&amp;aacute; Xay&lt;/h5&gt;\n&lt;p&gt;&lt;span style=&quot;color: #454545; font-family: Arial,sans-serif; font-size: 12px; line-height: 17px; text-align: justify;&quot;&gt;&lt;a href=&quot;http://muabanruoubia.com/wp-content/uploads/2010/04/06.jpg&quot;&gt;&lt;img class=&quot;alignleft size-full wp-image-703&quot; src=&quot;http://muabanruoubia.com/wp-content/uploads/2010/04/06.jpg&quot; border=&quot;0&quot; alt=&quot;06&quot; width=&quot;148&quot; height=&quot;146&quot; /&gt;&lt;/a&gt;H&amp;atilde;y tạo ấn tượng với b&amp;egrave; bạn bằng những ly Baileys Đ&amp;aacute; Xay&amp;nbsp; &amp;ndash; pha chế cực kỳ đơn giản nhưng cho vị ngon m&amp;aacute;t lạnh đến tuyệt vời.&lt;span&gt; &lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;Baileys&amp;nbsp; Đ&amp;aacute; Xay l&amp;agrave; một c&amp;aacute;ch đơn giản để g&amp;acirc;y ấn tượng với b&amp;egrave; bạn v&amp;agrave; l&amp;agrave;m dịu những th&amp;aacute;ng ng&amp;agrave;y oi bức. Nếu chỉ nh&amp;igrave;n sơ c&amp;oacute; lẽ ai cũng phải th&amp;aacute;n phục t&amp;agrave;i pha chế thức uống của bạn, nhưng thực ra, tất cả những g&amp;igrave; bạn cần l&amp;agrave;m l&amp;agrave; xay nhuyễn đ&amp;aacute; v&amp;agrave; Baileys để tạo n&amp;ecirc;n một thức uống ngon m&amp;aacute;t lạnh đến tuyệt vời.&amp;nbsp;&amp;nbsp;&lt;span&gt; &lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;margin: 0px; padding: 0px; text-decoration: underline;&quot;&gt;&lt;strong&gt;C&amp;ocirc;ng thức&lt;/strong&gt;&lt;/span&gt;&lt;span&gt; &lt;/span&gt;(lượng d&amp;ugrave;ng cho một người)&lt;/p&gt;\n&lt;p&gt;Bước 1: Thả 2 vi&amp;ecirc;n đ&amp;aacute; v&amp;agrave;o m&amp;aacute;y xay&lt;span&gt; &lt;/span&gt;&lt;br /&gt;Bước 2: Th&amp;ecirc;m v&amp;agrave;o 50ml Baileys&lt;br /&gt;Bước 3: Xay đến khi hỗn hợp ho&amp;agrave;n to&amp;agrave;n nhuyễn&lt;br /&gt;Bước 4: R&amp;oacute;t v&amp;agrave;o ly cao v&amp;agrave; sẵn s&amp;agrave;ng uống&lt;br /&gt;Thật tuyệt phải kh&amp;ocirc;ng?&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;margin: 0px; padding: 0px; text-decoration: underline;&quot;&gt;&lt;strong&gt;Mẹo nhỏ:&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;- Bạn n&amp;ecirc;n d&amp;ugrave;ng một m&amp;aacute;y xay c&amp;oacute; chức năng nghiền đ&amp;aacute; &amp;ndash; bạn c&amp;oacute; thể đổ th&amp;ecirc;m ch&amp;uacute;t nước để l&amp;agrave;m tan đ&amp;aacute; v&amp;agrave; xay đến khi đ&amp;aacute; nhuyễn nếu m&amp;aacute;y xay kh&amp;ocirc;ng đủ độ n&amp;oacute;ng.&lt;br /&gt;- Nếu m&amp;aacute;y xay bạn c&amp;oacute; chức năng hẹn giờ, h&amp;atilde;y c&amp;agrave;i đặt ở mức 10 gi&amp;acirc;y để ngăn đ&amp;aacute; chưa được xay nhuyễn tập trung ở bề mặt&lt;br /&gt;- Tr&amp;aacute;nh xay qu&amp;aacute; mức để thức uống kh&amp;ocirc;ng bị lo&amp;atilde;ng.&lt;/p&gt;\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\n&lt;h5&gt;Baileys Sweet Kiss&lt;/h5&gt;\n&lt;p&gt;&lt;span style=&quot;color: #454545; font-family: Arial,sans-serif; font-size: 12px; line-height: 17px; text-align: justify;&quot;&gt;&lt;a href=&quot;http://muabanruoubia.com/wp-content/uploads/2010/04/baley.jpg&quot;&gt;&lt;img class=&quot;alignleft size-full wp-image-704&quot; src=&quot;http://muabanruoubia.com/wp-content/uploads/2010/04/baley.jpg&quot; border=&quot;0&quot; alt=&quot;baley&quot; width=&quot;225&quot; height=&quot;150&quot; /&gt;&lt;/a&gt;Thưởng thức hương vị mới của sinh tố bơ với &lt;a href=&quot;http://muabanruoubia.com/&quot; title=&quot;rượu&quot;&gt;rượu&lt;/a&gt; Baileys, thật thơm ngon, quyến rũ.&lt;span&gt; &lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;margin: 0px; padding: 0px; text-decoration: underline;&quot;&gt;&lt;strong&gt;Chuẩn bị:&lt;/strong&gt;&lt;/span&gt;&lt;span&gt; &lt;/span&gt;&amp;frac14; quả bơ ch&amp;iacute;n, 30 ml Baileys, 30ml nước đường.&lt;span&gt; &lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;Bước 1: Cho tất cả nguy&amp;ecirc;n liệu tr&amp;ecirc;n v&amp;agrave;o m&amp;aacute;y xay sinh tố, xay với 01 muỗng đ&amp;aacute; b&amp;agrave;o nhuyễn.&lt;span&gt; &lt;/span&gt;&lt;br /&gt;Bước 2: R&amp;oacute;t ra ly cao.&lt;span&gt; &lt;/span&gt;&lt;br /&gt;Bước 3: Trang tr&amp;iacute; 1 tr&amp;aacute;i cherry ch&amp;iacute;n đỏ tr&amp;ecirc;n th&amp;agrave;nh ly.&lt;span&gt; &lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;C&amp;ugrave;ng Baileys Sweet Kiss chis sẻ những gi&amp;acirc;y ph&amp;uacute;t đầm ấm b&amp;ecirc;n nh&amp;oacute;m bạn th&amp;acirc;n hoặc những buổi tối l&amp;atilde;ng mạn d&amp;agrave;nh ri&amp;ecirc;ng cho hai người.&lt;span&gt; &lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;margin: 0px; padding: 0px; text-decoration: underline;&quot;&gt;&lt;strong&gt;Mẹo nhỏ:&lt;/strong&gt;&lt;/span&gt;&lt;br /&gt;- H&amp;atilde;y d&amp;ugrave;ng bơ tr&amp;aacute;i thật ch&amp;iacute;n sẽ tạo vị thơm ngon cho m&amp;oacute;n sinh tố bơ với Baileys.&lt;/p&gt;', '4e4f41941a91fbaileys.jpg', 1313679231, 'Sáng tạo cùng Baileys', 'Sáng tạo cùng Baileys', 'Sáng tạo cùng Baileys'),
(3, 2, 'Bailays-sự kết hợp giữa kem tươi với Whisky', 'bailayssu_ket_hop_giua_kem_tuoi_voi_whisky', 'Baileys – một loại thức uống nổi tiếng của tập đoàn Diageo (Ailen) đã chính thức có mặt trên thị trường Việt Nam từ ngày 8/12/2005 cùng với sự ra mắt của đại sứ toàn cầu của Baileys ông Peter O’Connor.', '&lt;p style=&quot;margin: 18px 0px; font-family: arial; font-size: 11pt; line-height: 21px;&quot;&gt;&lt;span style=&quot;text-align: left; font-weight: bold;&quot;&gt;Baileys &amp;ndash; một loại thức uống nổi tiếng của tập đo&amp;agrave;n Diageo (Ailen) đ&amp;atilde; ch&amp;iacute;nh thức c&amp;oacute; mặt tr&amp;ecirc;n thị trường Việt Nam từ ng&amp;agrave;y 8/12/2005 c&amp;ugrave;ng với sự ra mắt của đại sứ to&amp;agrave;n cầu của Baileys &amp;ocirc;ng Peter O&amp;rsquo;Connor.&lt;/span&gt;&lt;/p&gt;\n&lt;p style=&quot;margin: 18px 0px; font-family: arial; font-size: 11pt; line-height: 21px;&quot; align=&quot;justify&quot;&gt;&amp;nbsp;&lt;/p&gt;\n&lt;p style=&quot;margin: 18px 0px; font-family: arial; font-size: 11pt; line-height: 21px;&quot;&gt;&lt;a href=&quot;http://muabanruoubia.com/wp-content/uploads/2010/04/45177363-bailey.jpg&quot;&gt;&lt;img class=&quot;alignleft size-full wp-image-699&quot; src=&quot;http://muabanruoubia.com/wp-content/uploads/2010/04/45177363-bailey.jpg&quot; border=&quot;0&quot; alt=&quot;45177363-bailey&quot; width=&quot;165&quot; height=&quot;124&quot; /&gt;&lt;/a&gt;Việt Nam l&amp;agrave; thị trường thứ 5 tại ch&amp;acirc;u &amp;Aacute; m&amp;agrave; tập đo&amp;agrave;n Diageo ch&amp;iacute;nh thức đặt ch&amp;acirc;n đến nhưng lại được xem l&amp;agrave; thị trường tiềm năng thứ 3 sau Trung Quốc v&amp;agrave; Nhật Bản. C&amp;ocirc;ng thức chế biến của Baileys được xem l&amp;agrave; một b&amp;iacute; quyết th&amp;agrave;nh c&amp;ocirc;ng của loại thức uống n&amp;agrave;y. Mặc d&amp;ugrave; kh&amp;ocirc;ng sử dụng h&amp;oacute;a chất bảo quản nhưng Baileys c&amp;oacute; thời hạn sử dụng đến 2 năm kể từ ng&amp;agrave;y sản xuất m&amp;agrave; kh&amp;ocirc;ng cần bảo quản trong m&amp;ocirc;i trường lạnh. Bằng việc tạo ra những lớp ion dương t&amp;iacute;nh v&amp;agrave; &amp;acirc;m t&amp;iacute;nh tr&amp;ecirc;n bề mặt của &lt;a href=&quot;http://muabanruoubia.com/&quot; title=&quot;rượu&quot;&gt;rượu&lt;/a&gt; whisky v&amp;agrave; kem tươi, &lt;a href=&quot;http://muabanruoubia.com/tag/ruou-whisky/&quot; title=&quot;rượu whisky&quot;&gt;rượu whisky&lt;/a&gt; sẽ bao bọc lớp kem tươi b&amp;ecirc;n trong như một chất bảo quản tốt nhất. Baileys (với 14,7% độ cồn) kh&amp;ocirc;ng chỉ ph&amp;ugrave; hợp cho nam giới m&amp;agrave; ngay cả phụ nữ cũng dễ sử dụng với chất b&amp;eacute;o, ngọt của kem tươi trộn lẫn hương vị của rượu whisky.&lt;/p&gt;', '4e4f41c3dae0e45177363-bailey.jpg', 1313813048, 'Bailays-sự kết hợp giữa kem tươi với Whisky', 'Bailays-sự kết hợp giữa kem tươi với Whisky', 'Bailays-sự kết hợp giữa kem tươi với Whisky'),
(4, 2, 'Những loại cocktail làm từ rượu Bailey', 'nhung_loai_cocktail_lam_tu_ruou_bailey', 'B-52 (B52 hay Bifi) là tên gọi của một loại cocktail ngắn nhiều tầng bao gồm một phần rượu hương cà phê, một phần rượu Baileys Irish Cream và một phần rượu hương cam Le Grand Marnier. Khi được pha chế một cách thích hợp và chính xác thì ly rượu cocktail này sẽ có ba tầng ba màu rõ ràng. Sự chia tầng này do mật độ rượu khác nhau của ba loại rượu được dùng.', '&lt;div class=&quot;entry clearfix&quot;&gt;\n&lt;div class=&quot;content clearfix&quot;&gt;\n&lt;h1 id=&quot;\\&amp;quot;firstHeading\\&amp;quot;&quot;&gt;B-52 (cocktail)&lt;/h1&gt;\n&lt;p&gt;&lt;strong&gt;B-52&lt;/strong&gt; (&lt;strong&gt;B52&lt;/strong&gt; hay&amp;nbsp;&lt;strong&gt;Bifi&lt;/strong&gt;) l&amp;agrave; t&amp;ecirc;n gọi của một loại&amp;nbsp;&lt;a href=&quot;http://vi.wikipedia.org/w/index.php?title=Cocktail_ng%E1%BA%AFn&amp;amp;action=edit&amp;amp;redlink=1&quot; title=&quot;Cocktail ngắn (trang chưa được viết)&quot;&gt;cocktail ngắn&lt;/a&gt; nhiều tầng bao gồm một phần&amp;nbsp;&lt;a href=&quot;http://vi.wikipedia.org/w/index.php?title=R%C6%B0%E1%BB%A3u_h%C6%B0%C6%A1ng_c%C3%A0_ph%C3%AA&amp;amp;action=edit&amp;amp;redlink=1&quot; title=&quot;Rượu hương c&amp;agrave; ph&amp;ecirc; (trang chưa được viết)&quot;&gt;rượu hương c&amp;agrave; ph&amp;ecirc;&lt;/a&gt;, một phần &lt;a href=&quot;http://muabanruoubia.com/&quot; title=&quot;rượu&quot;&gt;rượu&lt;/a&gt;&amp;nbsp;&lt;a href=&quot;http://vi.wikipedia.org/w/index.php?title=Baileys_Irish_Cream&amp;amp;action=edit&amp;amp;redlink=1&quot; title=&quot;Baileys Irish Cream (trang chưa được viết)&quot;&gt;Baileys Irish Cream&lt;/a&gt; v&amp;agrave; một phần rượu hương cam&amp;nbsp;&lt;a href=&quot;http://vi.wikipedia.org/w/index.php?title=Le_Grand_Marnier&amp;amp;action=edit&amp;amp;redlink=1&quot; title=&quot;Le Grand Marnier (trang chưa được viết)&quot;&gt;Le Grand Marnier&lt;/a&gt;. Khi được pha chế một c&amp;aacute;ch th&amp;iacute;ch hợp v&amp;agrave; ch&amp;iacute;nh x&amp;aacute;c th&amp;igrave; ly rượu cocktail n&amp;agrave;y sẽ c&amp;oacute; ba tầng ba m&amp;agrave;u r&amp;otilde; r&amp;agrave;ng. Sự chia tầng n&amp;agrave;y do mật độ rượu kh&amp;aacute;c nhau của ba loại rượu được d&amp;ugrave;ng.&lt;/p&gt;\n&lt;h2&gt;&lt;span&gt;C&amp;aacute;ch pha chế&lt;/span&gt;&lt;/h2&gt;\n&lt;p&gt;&lt;span&gt;Trước ti&amp;ecirc;n đổ một phần rượu hương c&amp;agrave; ph&amp;ecirc; v&amp;agrave;o ly. Sau đ&amp;oacute; từ từ đổ một phần &lt;a href=&quot;http://ruoubaileys.com/&quot; title=&quot;rượu Baileys&quot;&gt;rượu Baileys&lt;/a&gt; v&amp;agrave;o qua 1 chiếc th&amp;igrave;a được đun n&amp;oacute;ng 1 c&amp;aacute;ch cẩn thận. V&amp;agrave; cuối c&amp;ugrave;ng th&amp;igrave; mới đổ phần rượu hương cam Le Grand Marnier. Khi pha th&amp;agrave;nh c&amp;ocirc;ng, ly rượu sẽ c&amp;oacute; tầng ba m&amp;agrave;u: n&amp;acirc;u, trắng v&amp;agrave; v&amp;agrave;ng từ đ&amp;aacute;y l&amp;ecirc;n.&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span&gt;Th&amp;ocirc;ng thường người ta uống B52 với một trong hai loại ly:&amp;nbsp;&lt;a href=&quot;http://en.wikipedia.org/wiki/Shot_glass&quot; title=&quot;en:Shot glass&quot;&gt;Shooter&lt;/a&gt; hoặc ly&amp;nbsp;&lt;a href=&quot;http://en.wikipedia.org/wiki/Wine_glass#Sherry_glass&quot; title=&quot;en:Wine glass&quot;&gt;Sherry&lt;/a&gt;&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span&gt;&lt;a href=&quot;http://muabanruoubia.com/2010/nhung-loai-cocktail-lam-tu-ruou-bailey/105px-zutaten_b52/&quot;&gt;&lt;img class=&quot;alignnone size-full wp-image-315&quot; src=&quot;http://muabanruoubia.com/wp-content/uploads/2010/04/105px-Zutaten_b52.jpg&quot; border=&quot;0&quot; alt=&quot;105px-Zutaten_b52&quot; width=&quot;105&quot; height=&quot;120&quot; /&gt;&lt;/a&gt;&lt;/span&gt;&lt;/p&gt;\n&lt;h1 id=&quot;\\&amp;quot;firstHeading\\&amp;quot;&quot;&gt;Bazooka (cocktail)&lt;/h1&gt;\n&lt;p&gt;&lt;strong&gt;Bazooka&lt;/strong&gt; l&amp;agrave; t&amp;ecirc;n gọi của một loại&amp;nbsp;&lt;a href=&quot;http://vi.wikipedia.org/w/index.php?title=Cocktail_ng%E1%BA%AFn&amp;amp;action=edit&amp;amp;redlink=1&quot; title=&quot;Cocktail ngắn (trang chưa được viết)&quot;&gt;cocktail ngắn&lt;/a&gt; nhiều tầng bao gồm một phần rượu hương&amp;nbsp;&lt;a href=&quot;http://vi.wikipedia.org/wiki/Cura%C3%A7ao&quot; title=&quot;Cura&amp;ccedil;ao&quot;&gt;Cura&amp;ccedil;ao&lt;/a&gt;, ba phần rượu&amp;nbsp;&lt;a href=&quot;http://vi.wikipedia.org/w/index.php?title=Baileys_Irish_Cream&amp;amp;action=edit&amp;amp;redlink=1&quot; title=&quot;Baileys Irish Cream (trang chưa được viết)&quot;&gt;Baileys Irish Cream&lt;/a&gt; v&amp;agrave; một phần rượu&amp;nbsp;&lt;a href=&quot;http://vi.wikipedia.org/wiki/Vodka&quot; title=&quot;Vodka&quot;&gt;Vodka&lt;/a&gt;. Khi được pha chế một c&amp;aacute;ch th&amp;iacute;ch hợp v&amp;agrave; ch&amp;iacute;nh x&amp;aacute;c th&amp;igrave; ly rượu cocktail n&amp;agrave;y sẽ c&amp;oacute; ba tầng ba m&amp;agrave;u r&amp;otilde; r&amp;agrave;ng. Sự chia tầng n&amp;agrave;y do mật độ rượu kh&amp;aacute;c nhau của ba loại rượu được d&amp;ugrave;ng.&lt;/p&gt;\n&lt;/div&gt;\n&lt;/div&gt;', '4e4f41dbd4cf5105px-Zutaten_b52.jpg', 1313813224, 'Những loại cocktail làm từ rượu Bailey', 'Những loại cocktail làm từ rượu Bailey', 'Những loại cocktail làm từ rượu Bailey'),
(5, 2, 'Rượu sữa BAILEYS', 'ruou_sua_baileys', 'Vị ngọt quyến rủ của kem tươi hòa nguyện với whisky Ailen hảo hạng cùng hương liệu tự nhiên chính là điều khiền Baileys trở nên đặc biệt. sang trọng một cách tinh tế, baileys xứng đáng là lời chúc phúc viên mãn nhất bạn gửi đến đối tác của mình.', '&lt;p&gt;Vị ngọt quyến rủ của kem tươi h&amp;ograve;a nguyện với &lt;a href=&quot;http://muabanruoubia.com/tag/whisky/&quot; title=&quot;whisky&quot;&gt;whisky&lt;/a&gt; Ailen hảo hạng c&amp;ugrave;ng hương liệu tự nhi&amp;ecirc;n ch&amp;iacute;nh l&amp;agrave; điều khiền &lt;a href=&quot;http://muabanruoubia.com/tag/baileys/&quot; title=&quot;Baileys&quot;&gt;Baileys&lt;/a&gt; trở n&amp;ecirc;n đặc biệt. sang trọng một c&amp;aacute;ch tinh tế, baileys xứng đ&amp;aacute;ng l&amp;agrave; lời ch&amp;uacute;c ph&amp;uacute;c vi&amp;ecirc;n m&amp;atilde;n nhất bạn gửi đến đối t&amp;aacute;c của m&amp;igrave;nh.&lt;/p&gt;\n&lt;p&gt;Hộp qu&amp;agrave; tặng với 1 ly cao cấp.&lt;a href=&quot;http://muabanruoubia.com/wp-content/uploads/2009/08/baileys.jpg&quot;&gt;&lt;img class=&quot;aligncenter size-full wp-image-130&quot; src=&quot;http://muabanruoubia.com/wp-content/uploads/2009/08/baileys.jpg&quot; border=&quot;0&quot; alt=&quot;baileys&quot; title=&quot;baileys&quot; width=&quot;325&quot; height=&quot;490&quot; /&gt;&lt;/a&gt;&lt;/p&gt;\n&lt;p&gt;SOMETIMES THE BEST GIFT IS THE TIME SHARED WITH FRIENDS&lt;/p&gt;\n&lt;p&gt;Baileys Original Irish Cream liqueur is a natural flavours. The great taste that makes every moment enjoyed together memorable. The perfect gift to share with friends.&lt;/p&gt;\n&lt;p&gt;Gift box includes 1 glass.&lt;/p&gt;', '4e4f41fde92fabaileys.jpg', 1313813317, 'Rượu sữa BAILEYS', 'Rượu sữa BAILEYS', 'Rượu sữa BAILEYS'),
(6, 2, '4 cách thưởng thức rượu sữa Baileys', '4_cach_thuong_thuc_ruou_sua_baileys', 'Baileys là nhãn hiệu rượu kem sữa Ailen được ưa chuộng nhất thế giới và đã được xếp vị trí số 1 trong ngành hàng rượu kem sữa và đứng thứ 6 trong 10 nhãn hiệu rượu có uy tín nhất toàn cầu. Baileys rất thích hợp cho những buổi tiệc vui tại nhà với 4 cách uống đơn giản.', '&lt;div id=&quot;quote&quot;&gt;\n&lt;p align=&quot;justify&quot;&gt;&lt;a href=&quot;http://muabanruoubia.com/tag/baileys/&quot; title=&quot;Baileys&quot;&gt;Baileys&lt;/a&gt; l&amp;agrave; nh&amp;atilde;n hiệu &lt;a href=&quot;http://muabanruoubia.com/&quot; title=&quot;rượu&quot;&gt;rượu&lt;/a&gt; kem sữa Ailen được ưa chuộng nhất thế giới v&amp;agrave; đ&amp;atilde; được xếp vị tr&amp;iacute; số 1 trong ng&amp;agrave;nh h&amp;agrave;ng rượu kem sữa v&amp;agrave; đứng thứ 6 trong 10 nh&amp;atilde;n hiệu rượu c&amp;oacute; uy t&amp;iacute;n nhất to&amp;agrave;n cầu. Baileys rất th&amp;iacute;ch hợp cho những buổi tiệc vui tại nh&amp;agrave; với 4 c&amp;aacute;ch uống đơn giản.&lt;/p&gt;\n&lt;/div&gt;\n&lt;p align=&quot;justify&quot;&gt;Baileys được sản xuất với những nguy&amp;ecirc;n liệu c&amp;oacute; chất lượng tốt nhất: kem sữa tươi từ Ailen, &lt;a href=&quot;http://muabanruoubia.com/tag/whisky/&quot; title=&quot;whisky&quot;&gt;whisky&lt;/a&gt; Ailen, rượu Ailen, v&amp;agrave; chocolate. Kem sữa được bảo quản ho&amp;agrave;n to&amp;agrave;n tự nhi&amp;ecirc;n, bằng quy tr&amp;igrave;nh phủ cồn tr&amp;ecirc;n kem đ&amp;atilde; được đăng k&amp;yacute; đảm bảo t&amp;iacute;nh thanh khiết của kem sữa.&lt;/p&gt;\n&lt;p align=&quot;justify&quot;&gt;Vị ngon hảo hạng của Baileys được đảm bảo đồng bộ bằng c&amp;aacute;ch kết hợp những nguy&amp;ecirc;n liệu tốt nhất với c&amp;ocirc;ng thức độc đ&amp;aacute;o ở hai nơi c&amp;oacute; c&amp;ocirc;ng nghệ sản xuất ti&amp;ecirc;n tiến nhất l&amp;agrave; Dublin and Belfast. Baileys kh&amp;ocirc;ng chứa chất bảo quản v&amp;agrave; c&amp;oacute; thể được giữ trong v&amp;ograve;ng 24 th&amp;aacute;ng.&lt;/p&gt;\n&lt;p align=&quot;justify&quot;&gt;Baileys rất th&amp;iacute;ch hợp cho những buổi tiệc vui tại nh&amp;agrave; với 4 c&amp;aacute;ch uống đơn giản:&lt;/p&gt;\n&lt;p align=&quot;justify&quot;&gt;- Baileys với đ&amp;aacute;: 50ml Baileys r&amp;oacute;t v&amp;agrave;o một ly tumbler chứa đầy đ&amp;aacute;.&lt;/p&gt;\n&lt;p align=&quot;justify&quot;&gt;- Baileys lắc: 50ml Baileys lắc với đ&amp;aacute; bằng b&amp;igrave;nh lắc cocktail, r&amp;oacute;t v&amp;agrave;o ly Martini v&amp;agrave; trang tr&amp;iacute; với bột chocolate.&lt;/p&gt;\n&lt;p align=&quot;justify&quot;&gt;- Baileys xay: 50ml Baileys xay với 3 vi&amp;ecirc;n đ&amp;aacute; trong m&amp;aacute;y xay cho đến khi nhuyễn (kh&amp;ocirc;ng c&amp;ograve;n thấy đ&amp;aacute; nữa) v&amp;agrave; r&amp;oacute;t v&amp;agrave;o một ly cao.&lt;/p&gt;\n&lt;p&gt;- Baileys với c&amp;agrave; ph&amp;ecirc;: R&amp;oacute;t c&amp;agrave; ph&amp;ecirc; v&amp;agrave; 35ml Baileys v&amp;agrave;o một ly l&amp;aacute;t t&amp;ecirc; lớn v&amp;agrave; phủ tr&amp;ecirc;n mặt một lớp bọt kem.&lt;/p&gt;', '4e4f421d28ddabaileys.jpg', 1313813498, '4 cách thưởng thức rượu sữa Baileys', '4 cách thưởng thức rượu sữa Baileys', '4 cách thưởng thức rượu sữa Baileys'),
(7, 2, 'Cách chọn rượu champagne', 'cach_chon_ruou_champagne', 'Champagne là một dạng vang nổ, thuộc loại rượu nhẹ, mùi dịu và vị ngọt, được sản xuất bằng cách tạo ra sự lên men thứ cấp trong chai chứa rượu vang để thực hiện sự cacbonat hóa, có xuất xứ từ vùng Champagne của Pháp. Champagne thường dùng để khai vị hoặc uống trước khi đi ngủ tạo sự hưng phấn.', '&lt;p&gt;&lt;span style=&quot;color: #252525; font-family: ''Times New Roman'',Times,serif; font-size: 14px;&quot;&gt;&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;span style=&quot;font-size: 12px; font-family: tahoma;&quot;&gt;&lt;strong&gt;Champagne&lt;/strong&gt;&lt;span&gt; &lt;/span&gt;l&amp;agrave; một dạng vang nổ, thuộc loại &lt;a href=&quot;http://muabanruoubia.com/&quot; title=&quot;rượu&quot;&gt;rượu&lt;/a&gt; nhẹ, m&amp;ugrave;i dịu v&amp;agrave; vị ngọt, được sản xuất bằng c&amp;aacute;ch tạo ra sự l&amp;ecirc;n men thứ cấp trong chai chứa rượu vang để thực hiện sự cacbonat h&amp;oacute;a, c&amp;oacute; xuất xứ từ v&amp;ugrave;ng Champagne của Ph&amp;aacute;p. Champagne thường d&amp;ugrave;ng để khai vị hoặc uống trước khi đi ngủ tạo sự hưng phấn.&lt;/span&gt;&lt;br /&gt; &lt;span style=&quot;font-size: 12px; font-family: tahoma;&quot;&gt;Chất lượng của Champagne t&amp;ugrave;y thuộc v&amp;agrave;o giống nho, thời tiết lẫn c&amp;aacute;ch thu hoạch v&amp;agrave; cất ủ, ngo&amp;agrave;i ra một yếu tố quan trọng tạo n&amp;ecirc;n vị ngon của Champagne đ&amp;oacute; l&amp;agrave; lượng đường được th&amp;ecirc;m v&amp;agrave;o.&lt;span&gt; &lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;strong&gt;&lt;em&gt;&lt;span style=&quot;font-size: 12px; font-family: tahoma;&quot;&gt;Để chọn được chai &lt;a href=&quot;http://muabanruoubia.com/cat/ruou-ngoai/ruou-champagne/&quot; title=&quot;rượu Champagne&quot;&gt;rượu Champagne&lt;/a&gt; ngon, c&amp;aacute;c bạn h&amp;atilde;y l&amp;agrave;m theo chỉ dẫn của c&amp;aacute;c chuy&amp;ecirc;n gia ehow sau đ&amp;acirc;y:&lt;/span&gt;&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px; font-family: tahoma;&quot;&gt;- H&amp;atilde;y nếm thử m&amp;ugrave;i vị của c&amp;aacute;c loại Champagne kh&amp;aacute;c nhau để xem loại n&amp;agrave;o l&amp;ocirc;i cuốn bạn nhất.&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px; font-family: tahoma;&quot;&gt;- H&amp;atilde;y đi mua c&amp;ugrave;ng v&amp;agrave;i người bạn, tham khảo &amp;yacute; kiến của họ sau khi nếm thử, hoặc c&amp;oacute; thể xin lời tư vấn trực tiếp từ&amp;nbsp; người b&amp;aacute;n h&amp;agrave;ng .&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px; font-family: tahoma;&quot;&gt;- H&amp;atilde;y bổ sung kiến thức về c&amp;aacute;c loại rượu Champagne để biết c&amp;aacute;ch ph&amp;acirc;n biệt giữa loại Champagne của Ph&amp;aacute;p với rượu &lt;a href=&quot;http://muabanruoubia.com/2010/vang-sui-bot/&quot; title=&quot;vang sủi bọt&quot;&gt;vang sủi bọt&lt;/a&gt; của California cũng như c&amp;aacute;c đặc t&amp;iacute;nh của rượu: kh&amp;ocirc; hay ngọt, nhiều kem hay nhiều bọt, bọt sủi tăm hay sủi nhiều v&amp;agrave; ghi nhớ rằng c&amp;aacute;c đặc t&amp;iacute;nh n&amp;agrave;y sẽ quyết định đến khẩu vị của bạn rất nhiều đấy.&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px; font-family: tahoma;&quot;&gt;- T&amp;igrave;m hiểu c&amp;aacute;c thương hiệu rượu Champagne nổi tiếng cũng như c&amp;aacute;ch l&amp;agrave;m v&amp;agrave; năm sản xuất. bởi Champagne c&amp;agrave;ng được ủ kỹ, cất l&amp;acirc;u trong b&amp;oacute;ng tối c&amp;agrave;ng trở n&amp;ecirc;n ngon hơn&lt;span&gt; &lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px; font-family: tahoma;&quot;&gt;- Một chai Champagne ngon kh&amp;ocirc;ng chỉ được quyết định bởi khẩu vị của bạn m&amp;agrave; vỏ chai rượu ấy c&amp;ograve;n phải mang n&amp;eacute;t h&amp;agrave;i h&amp;ograve;a, thẩm mĩ cao, tuy nhi&amp;ecirc;n cũng cần ch&amp;uacute; &amp;yacute; đến ng&amp;acirc;n s&amp;aacute;ch của m&amp;igrave;nh để lựa được loại rượu ưng &amp;yacute;.&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span&gt;&lt;strong&gt;&lt;em&gt;&lt;span style=&quot;font-size: 12px; font-family: tahoma;&quot;&gt;Lưu &amp;yacute;&lt;/span&gt;&lt;/em&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px; font-family: tahoma;&quot;&gt;- Để tr&amp;aacute;nh nhầm lẫn với loại rượu bọt của California, c&amp;aacute;c bạn n&amp;ecirc;n nhớ Champagne &amp;ldquo; đ&amp;iacute;ch thực&amp;rdquo; phải c&amp;oacute; nguồn gốc từ c&amp;aacute;c v&amp;ugrave;ng trồng nho chuy&amp;ecirc;n biệt của nước Ph&amp;aacute;p.&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px; font-family: tahoma;&quot;&gt;- H&amp;atilde;y ch&amp;uacute; &amp;yacute; năm đ&amp;oacute;ng chai tr&amp;ecirc;n nh&amp;atilde;n m&amp;aacute;c khi mua Champagne v&amp;agrave; t&amp;igrave;m hiểu về c&amp;aacute;c vụ m&amp;ugrave;a thu hoạch nho th&amp;agrave;nh c&amp;ocirc;ng, bởi n&amp;oacute; ảnh hưởng rất nhiều đến chất lượng của rượu. V&amp;iacute; dụ m&amp;ugrave;a thu hoạch nho bội thu rất nổi tiếng ở Ph&amp;aacute;p l&amp;agrave; v&amp;agrave;o năm 1990 v&amp;agrave; 1995, trong khi vụ m&amp;ugrave;a bội thu ở California l&amp;agrave; năm 1990, 1991, 1992, 1994 v&amp;agrave; 1995.&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px; font-family: tahoma;&quot;&gt;- C&amp;oacute; thể nhấm nh&amp;aacute;p th&amp;ecirc;m một ch&amp;uacute;t hoa quả như dưa, đ&amp;agrave;o, mơ, t&amp;aacute;o xanh hay b&amp;aacute;nh m&amp;igrave; nướng khi thưởng thức loại rượu n&amp;agrave;y.&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;em&gt;&lt;span style=&quot;font-size: 12px; font-family: tahoma;&quot;&gt;Ngo&amp;agrave;i ra để thực sự thưởng thức hết được c&amp;aacute;i ngon của champagne bạn n&amp;ecirc;n nhớ:&lt;/span&gt;&lt;br /&gt; &lt;/em&gt;&lt;br /&gt; &lt;span style=&quot;font-size: 12px; font-family: tahoma;&quot;&gt;- Cất chai trong hầm rượu từ 11˚C đến 13˚C.&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px; font-family: tahoma;&quot;&gt;- Khi chuẩn bị uống, lấy chai từ hầm l&amp;ecirc;n, lau sạch bụi rồi ng&amp;acirc;m v&amp;agrave;o x&amp;ocirc; nước sạch đổ đầy đến vai chai, th&amp;ecirc;m v&amp;agrave;o v&amp;agrave;i cục đ&amp;aacute; lạnh. Đ&amp;oacute; l&amp;agrave; c&amp;aacute;ch tốt nhất để ướp lạnh Champagne, đừng cho v&amp;agrave;o tủ lạnh.&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px; font-family: tahoma;&quot;&gt;- Ly uống champagne phải được rửa sạch bằng nước tự nhi&amp;ecirc;n, kh&amp;ocirc;ng d&amp;ugrave;ng bất cứ loại x&amp;agrave; ph&amp;ograve;ng, chất tẩy n&amp;agrave;o. &amp;Uacute;p ly cho r&amp;aacute;o nước rồi d&amp;ugrave;ng khăn sạch thật kh&amp;ocirc; để lau, khi n&amp;agrave;o ly b&amp;oacute;ng l&amp;aacute;ng mới r&amp;oacute;t Champagne v&amp;agrave;o.&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;em&gt;&lt;span style=&quot;font-size: 12px; font-family: tahoma;&quot;&gt;&lt;strong&gt;C&amp;aacute;ch chọn ly đối với Champagne:&lt;/strong&gt;&lt;/span&gt;&lt;/em&gt;&lt;/p&gt;\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px; font-family: tahoma;&quot;&gt;- Ly uống Champagne thường c&amp;oacute; h&amp;igrave;nh n&amp;oacute;n hoặc h&amp;igrave;nh parabol th&amp;agrave;nh cao, miệng ly hơi uốn cong v&amp;agrave;o trong gần giống h&amp;igrave;nh hoa tuy lip để lưu lại những bọt nhỏ ly ti hay hương thơm của rượu. Khi uống d&amp;ugrave;ng ng&amp;oacute;n tay giữ cuống ly, nếu cầm bằng cả hai tay, hơi n&amp;oacute;ng của b&amp;agrave;n tay sẽ l&amp;agrave;m rượu n&amp;oacute;ng l&amp;ecirc;n v&amp;agrave; l&amp;agrave;m mất đi hương vị của rượu. Champagne ướp lạnh ở nhiệt độ 7-9 độ C, khi uống thường được r&amp;oacute;t 2/3 ly.&lt;span&gt; &lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px; font-family: tahoma;&quot;&gt;- Với c&amp;aacute;c bữa tiệc lớn, đ&amp;ocirc;ng người, hay tiệc đứng ly được xếp theo h&amp;igrave;nh th&amp;aacute;p để r&amp;oacute;t nhiều ly c&amp;ugrave;ng một l&amp;uacute;c, do đ&amp;oacute; thường sử dụng loại ly ngắn, miệng rộng.&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px; font-family: tahoma;&quot;&gt;- Với c&amp;aacute;c bữa tiệc &amp;iacute;t người, loại ly được sử dụng l&amp;agrave; loại ly c&amp;oacute; h&amp;igrave;nh dạng thu&amp;ocirc;n d&amp;agrave;i, đường k&amp;iacute;nh nhỏ để giữ cho rượu sủi tăm l&amp;acirc;u hơn, tạo cảm gi&amp;aacute;c th&amp;uacute; vị hơn.&lt;span&gt; &lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px; font-family: tahoma;&quot;&gt;Hi vọng những lời tư vấn tr&amp;ecirc;n sẽ hữu &amp;iacute;ch với c&amp;aacute;c bạn!&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-size: 12px; font-family: tahoma;&quot;&gt;&lt;a href=&quot;http://muabanruoubia.com/wp-content/uploads/2010/05/BRUT_IMPERIAL_FOIL__FLUTE_low1.jpg&quot;&gt;&lt;img class=&quot;aligncenter size-full wp-image-765&quot; src=&quot;http://muabanruoubia.com/wp-content/uploads/2010/05/BRUT_IMPERIAL_FOIL__FLUTE_low1.jpg&quot; border=&quot;0&quot; alt=&quot;BRUT_IMPERIAL_FOIL_&amp;amp;_FLUTE_low&quot; width=&quot;328&quot; height=&quot;492&quot; /&gt;&lt;/a&gt;&lt;/span&gt;&lt;/p&gt;', '4e4f4acacce59BRUT_IMPERIAL_FOIL__FLUTE_low1.jpg', 1313813567, 'Cách chọn rượu champagne', 'Cách chọn rượu champagne', 'Cách chọn rượu champagne'),
(8, 2, 'Tìm hiểu về Champagne', 'tim_hieu_ve_champagne', 'Sâm panh là một dạng vang nổ được sản xuất bằng cách tạo ra sự lên men thứ cấp trong chai chứarượu vang để thực hiện sự cacbonat hóa. Nó được đặt tên theo khu vực Champagne của Pháp. Trong khi thuật ngữ “champagne” (sâm panh) thông thường được các nhà sản xuất vang nổ tại các khu vực khác trên thế giới sử dụng để chỉ sản phẩm của mình thì có nhiều ý kiến cho rằng nó nên được sử dụng một cách chính xác để chỉ tới các loại rượu vang được sản xuất tại khu vực Champagne. Nguyên tắc này được coi trọng tại Liên minh châu Âu bằng quan hệ pháp lý của tên gọi được bảo hộ về nguồn gốc (PDO).', '&lt;p&gt;&lt;span style=&quot;font-family: sans-serif; font-size: 13px; line-height: 19px;&quot;&gt;&lt;strong&gt;S&amp;acirc;m panh&lt;/strong&gt;&lt;span&gt; &lt;/span&gt;l&amp;agrave; một dạng&lt;span&gt; &lt;/span&gt;&lt;a href=&quot;http://muabanruoubia.com/w/index.php?title=Vang_n%E1%BB%95&amp;amp;action=edit&amp;amp;redlink=1&quot; title=&quot;Vang nổ (trang chưa được viết)&quot;&gt;vang nổ&lt;/a&gt;&lt;span&gt; &lt;/span&gt;được sản xuất bằng c&amp;aacute;ch tạo ra sự&lt;span&gt; &lt;/span&gt;&lt;a href=&quot;http://muabanruoubia.com/w/index.php?title=L%C3%AAn_men_th%E1%BB%A9_c%E1%BA%A5p&amp;amp;action=edit&amp;amp;redlink=1&quot; title=&quot;L&amp;ecirc;n men thứ cấp (trang chưa được viết)&quot;&gt;l&amp;ecirc;n men thứ cấp&lt;/a&gt;&lt;span&gt; &lt;/span&gt;trong chai chứa&lt;a href=&quot;http://muabanruoubia.com/wiki/R%C6%B0%E1%BB%A3u_vang&quot; title=&quot;Rượu vang&quot;&gt;rượu vang&lt;/a&gt;&lt;span&gt; &lt;/span&gt;để thực hiện sự&lt;span&gt; &lt;/span&gt;&lt;a href=&quot;http://muabanruoubia.com/w/index.php?title=Cacbonat_h%C3%B3a&amp;amp;action=edit&amp;amp;redlink=1&quot; title=&quot;Cacbonat h&amp;oacute;a (trang chưa được viết)&quot;&gt;cacbonat h&amp;oacute;a&lt;/a&gt;. N&amp;oacute; được đặt t&amp;ecirc;n theo khu vực&lt;span&gt; &lt;/span&gt;&lt;a href=&quot;http://muabanruoubia.com/w/index.php?title=Champagne_%28t%E1%BB%89nh%29&amp;amp;action=edit&amp;amp;redlink=1&quot; title=&quot;Champagne (tỉnh) (trang chưa được viết)&quot;&gt;Champagne&lt;/a&gt;&lt;span&gt; &lt;/span&gt;của&lt;span&gt; &lt;/span&gt;&lt;a href=&quot;http://muabanruoubia.com/wiki/Ph%C3%A1p&quot; title=&quot;Ph&amp;aacute;p&quot;&gt;Ph&amp;aacute;p&lt;/a&gt;. Trong khi thuật ngữ &amp;ldquo;champagne&amp;rdquo; (s&amp;acirc;m panh) th&amp;ocirc;ng thường được c&amp;aacute;c nh&amp;agrave; sản xuất vang nổ tại c&amp;aacute;c khu vực kh&amp;aacute;c tr&amp;ecirc;n thế giới sử dụng để chỉ sản phẩm của m&amp;igrave;nh th&amp;igrave; c&amp;oacute; nhiều &amp;yacute; kiến cho rằng n&amp;oacute; n&amp;ecirc;n được sử dụng một c&amp;aacute;ch ch&amp;iacute;nh x&amp;aacute;c để chỉ tới c&amp;aacute;c loại &lt;a href=&quot;http://muabanruoubia.com/&quot; title=&quot;rượu&quot;&gt;rượu&lt;/a&gt; vang được sản xuất tại khu vực Champagne. Nguy&amp;ecirc;n tắc n&amp;agrave;y được coi trọng tại&lt;span&gt; &lt;/span&gt;&lt;a href=&quot;http://muabanruoubia.com/wiki/Li%C3%AAn_minh_ch%C3%A2u_%C3%82u&quot; title=&quot;Li&amp;ecirc;n minh ch&amp;acirc;u &amp;Acirc;u&quot;&gt;Li&amp;ecirc;n minh ch&amp;acirc;u &amp;Acirc;u&lt;/a&gt;&lt;span&gt; &lt;/span&gt;bằng quan hệ ph&amp;aacute;p l&amp;yacute; của&lt;span&gt; &lt;/span&gt;&lt;a href=&quot;http://muabanruoubia.com/w/index.php?title=T%C3%AAn_g%E1%BB%8Di_%C4%91%C6%B0%E1%BB%A3c_b%E1%BA%A3o_h%E1%BB%99_v%E1%BB%81_ngu%E1%BB%93n_g%E1%BB%91c&amp;amp;action=edit&amp;amp;redlink=1&quot; title=&quot;T&amp;ecirc;n gọi được bảo hộ về nguồn gốc (trang chưa được viết)&quot;&gt;t&amp;ecirc;n gọi được bảo hộ về nguồn gốc&lt;/a&gt;&lt;span&gt; &lt;/span&gt;(PDO).&lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&lt;span style=&quot;font-family: sans-serif; font-size: 13px; line-height: 19px;&quot;&gt;&lt;a href=&quot;http://muabanruoubia.com/wp-content/uploads/2010/04/200px-Champagne.jpg&quot;&gt;&lt;img class=&quot;aligncenter size-full wp-image-667&quot; src=&quot;http://muabanruoubia.com/wp-content/uploads/2010/04/200px-Champagne.jpg&quot; border=&quot;0&quot; alt=&quot;200px-Champagne&quot; width=&quot;200&quot; height=&quot;267&quot; /&gt;&lt;/a&gt;&lt;br /&gt; &lt;/span&gt;&lt;/p&gt;\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\n&lt;h2&gt;&lt;span&gt;Lịch sử&lt;/span&gt;&lt;/h2&gt;\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\n&lt;p style=&quot;margin: 0.4em 0px 0.5em; line-height: 1.5em;&quot;&gt;V&amp;ugrave;ng đất Champagne của Ph&amp;aacute;p với sự biến động của&lt;span&gt; &lt;/span&gt;&lt;a href=&quot;http://muabanruoubia.com/wiki/Th%E1%BB%9Di_ti%E1%BA%BFt&quot; title=&quot;Thời tiết&quot;&gt;thời tiết&lt;/a&gt;&lt;span&gt; &lt;/span&gt;hết sức khắc nghiệt k&amp;eacute;o theo sự thất thường của những vụ nho, khi th&amp;igrave; bội thu, l&amp;uacute;c lại hết sức khan hiếm. Nhưng ch&amp;iacute;nh sự khắc nghiệt của thời tiết nơi đ&amp;acirc;y đ&amp;atilde; tạo n&amp;ecirc;n những tr&amp;aacute;i nho c&amp;oacute; hương vị đặc biệt thơm ngon.&lt;/p&gt;\n&lt;p style=&quot;margin: 0.4em 0px 0.5em; line-height: 1.5em;&quot;&gt;&lt;a href=&quot;http://muabanruoubia.com/2010/ruou-nho/&quot; title=&quot;Rượu nho&quot;&gt;Rượu nho&lt;/a&gt; của v&amp;ugrave;ng Champagne được biết đến đầu ti&amp;ecirc;n trong giới qu&amp;yacute; tộc khi vị linh mục t&amp;ecirc;n l&amp;agrave;&lt;span&gt; &lt;/span&gt;&lt;a href=&quot;http://muabanruoubia.com/w/index.php?title=Saint_Remi&amp;amp;action=edit&amp;amp;redlink=1&quot; title=&quot;Saint Remi (trang chưa được viết)&quot;&gt;Saint Remi&lt;/a&gt;v&amp;ugrave;ng&lt;span&gt; &lt;/span&gt;&lt;a href=&quot;http://muabanruoubia.com/w/index.php?title=Reim&amp;amp;action=edit&amp;amp;redlink=1&quot; title=&quot;Reim (trang chưa được viết)&quot;&gt;Reim&lt;/a&gt;&lt;span&gt; &lt;/span&gt;đ&amp;atilde; d&amp;ugrave;ng rượu nho của v&amp;ugrave;ng Champagne l&amp;agrave;m lễ rửa tội cho vua&lt;span&gt; &lt;/span&gt;&lt;a href=&quot;http://muabanruoubia.com/w/index.php?title=Clovis&amp;amp;action=edit&amp;amp;redlink=1&quot; title=&quot;Clovis (trang chưa được viết)&quot;&gt;Clovis&lt;/a&gt;&lt;span&gt; &lt;/span&gt;trong m&amp;ugrave;a&lt;span&gt; &lt;/span&gt;&lt;a href=&quot;http://muabanruoubia.com/wiki/Gi%C3%A1ng_sinh&quot; title=&quot;Gi&amp;aacute;ng sinh&quot;&gt;Gi&amp;aacute;ng sinh&lt;/a&gt;&lt;span&gt; &lt;/span&gt;năm 496. Năm 1285, khi người thừa kế v&amp;ugrave;ng Champagne v&amp;agrave; v&amp;ugrave;ng&lt;span&gt; &lt;/span&gt;&lt;a href=&quot;http://muabanruoubia.com/wiki/Navarre&quot; title=&quot;Navarre&quot;&gt;Navarre&lt;/a&gt;&lt;span&gt; &lt;/span&gt;t&amp;ecirc;n l&amp;agrave;&lt;span&gt; &lt;/span&gt;&lt;a href=&quot;http://muabanruoubia.com/w/index.php?title=Jeanne&amp;amp;action=edit&amp;amp;redlink=1&quot; title=&quot;Jeanne (trang chưa được viết)&quot;&gt;Jeanne&lt;/a&gt;&lt;span&gt; &lt;/span&gt;kết h&amp;ocirc;n với&lt;span&gt; &lt;/span&gt;&lt;a href=&quot;http://muabanruoubia.com/w/index.php?title=Philip&amp;amp;action=edit&amp;amp;redlink=1&quot; title=&quot;Philip (trang chưa được viết)&quot;&gt;Philip&lt;/a&gt;, người thừa kế&lt;span&gt; &lt;/span&gt;&lt;a href=&quot;http://muabanruoubia.com/w/index.php?title=Ngai_v%C3%A0ng&amp;amp;action=edit&amp;amp;redlink=1&quot; title=&quot;Ngai v&amp;agrave;ng (trang chưa được viết)&quot;&gt;ngai v&amp;agrave;ng&lt;/a&gt;&lt;span&gt; &lt;/span&gt;nước Ph&amp;aacute;p, th&amp;igrave; v&amp;ugrave;ng Champagne trở th&amp;agrave;nh ph&amp;ecirc;n dậu của Ph&amp;aacute;p quốc, v&amp;agrave; rượu nho của v&amp;ugrave;ng n&amp;agrave;y dần trở th&amp;agrave;nh rượu của c&amp;aacute;c &amp;ocirc;ng ho&amp;agrave;ng b&amp;agrave; ch&amp;uacute;a v&amp;agrave; giới&lt;span&gt; &lt;/span&gt;&lt;a href=&quot;http://muabanruoubia.com/w/index.php?title=Qu%C3%BD_t%E1%BB%99c&amp;amp;action=edit&amp;amp;redlink=1&quot; title=&quot;Qu&amp;yacute; tộc (trang chưa được viết)&quot;&gt;qu&amp;yacute; tộc&lt;/a&gt;&lt;span&gt; &lt;/span&gt;Ph&amp;aacute;p. Đến thời&lt;span&gt; &lt;/span&gt;&lt;a href=&quot;http://muabanruoubia.com/wiki/Ph%E1%BB%A5c_h%C6%B0ng&quot; title=&quot;Phục hưng&quot;&gt;Phục hưng&lt;/a&gt;, khi những đo&amp;agrave;n qu&amp;acirc;n của&lt;span&gt; &lt;/span&gt;&lt;a href=&quot;http://muabanruoubia.com/wiki/Julius_Caesar&quot; title=&quot;Julius Caesar&quot;&gt;Julius Caesar&lt;/a&gt;&lt;span&gt; &lt;/span&gt;kh&amp;aacute;m ph&amp;aacute; ra v&amp;ugrave;ng đất đặc biệt n&amp;agrave;y, c&amp;aacute;c vườn nho dần mở rộng th&amp;agrave;nh những c&amp;aacute;nh đồng nho bạt ng&amp;agrave;n v&amp;agrave; việc bu&amp;ocirc;n b&amp;aacute;n rượu ra nước ngo&amp;agrave;i bắt đầu ph&amp;aacute;t triển nhanh ch&amp;oacute;ng.&lt;/p&gt;\n&lt;p style=&quot;margin: 0.4em 0px 0.5em; line-height: 1.5em;&quot;&gt;Trải theo thời gian đến tận&lt;span&gt; &lt;/span&gt;&lt;a href=&quot;http://muabanruoubia.com/wiki/Th%E1%BA%BF_k%E1%BB%B7_17&quot; title=&quot;Thế kỷ 17&quot;&gt;thế kỷ 17&lt;/a&gt;, c&amp;aacute;c nh&amp;agrave; l&amp;agrave;m rượu, bằng sự quan s&amp;aacute;t qu&amp;aacute; tr&amp;igrave;nh l&amp;ecirc;n men tự nhi&amp;ecirc;n của rượu ki&amp;ecirc;n tr&amp;igrave; trong nhiều năm, đ&amp;atilde; t&amp;igrave;m ra những quy luật v&amp;agrave; b&amp;iacute; quyết ri&amp;ecirc;ng để pha trộn c&amp;aacute;c giống nho, c&amp;aacute;c loại rượu được ủ trong thời gian kh&amp;aacute;c nhau, để cố định được hương vị, độ trong, những hạt bọt sủi lăn tăn trong rượu. Rượu s&amp;acirc;m panh, kể từ đ&amp;oacute;, đ&amp;atilde; l&amp;agrave;m say l&amp;ograve;ng kh&amp;ocirc;ng biết bao nhi&amp;ecirc;u thế hệ với chất lượng ổn định như ch&amp;uacute;ng ta thấy ng&amp;agrave;y nay.&lt;/p&gt;\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\n&lt;h2&gt;&lt;span&gt;&lt;br /&gt; Danh hiệu rượu s&amp;acirc;m panh&lt;/span&gt;&lt;/h2&gt;\n&lt;p style=&quot;margin: 0.4em 0px 0.5em; line-height: 1.5em;&quot;&gt;Chỉ c&amp;oacute; &lt;a href=&quot;http://muabanruoubia.com/cat/ruou-ngoai/ruou_vang/&quot; title=&quot;rượu vang&quot;&gt;rượu vang&lt;/a&gt; sản sinh tr&amp;ecirc;n v&amp;ugrave;ng đất Champagne mới được gọi l&amp;agrave; rượu s&amp;acirc;m panh. V&amp;agrave; nh&amp;atilde;n m&amp;aacute;c t&amp;ecirc;n rượu đ&amp;atilde; được ch&amp;iacute;nh phủ Ph&amp;aacute;p bảo hộ. C&amp;aacute;c loại rượu nhẹ sản xuất từ nho tr&amp;ecirc;n những v&amp;ugrave;ng kh&amp;aacute;c của l&amp;atilde;nh thổ Ph&amp;aacute;p được gọi bằng t&amp;ecirc;n vang nổ (&lt;em&gt;sparkling wine&lt;/em&gt;). Nhằm giữ được hương vị, chất lượng của rượu s&amp;acirc;m panh, ch&amp;iacute;nh phủ Ph&amp;aacute;p cũng quy định rất nghi&amp;ecirc;m ngặt c&amp;aacute;c loại nho được ph&amp;eacute;p trồng tr&amp;ecirc;n v&amp;ugrave;ng đất n&amp;agrave;y, v&amp;agrave; việc chăm s&amp;oacute;c nho, phương ph&amp;aacute;p l&amp;agrave;m rượu, thời gian cất giữ rượu, ranh giới v&amp;ugrave;ng trồng nho cũng được kiểm so&amp;aacute;t nghi&amp;ecirc;m ngặt.&lt;/p&gt;\n&lt;h2&gt;&lt;span style=&quot;float: right; margin-left: 5px; font-size: 13px;&quot;&gt;&lt;/span&gt;&lt;span&gt;C&amp;aacute;c loại rượu&lt;/span&gt;&lt;/h2&gt;\n&lt;p style=&quot;margin: 0.4em 0px 0.5em; line-height: 1.5em;&quot;&gt;Rượu S&amp;acirc;m panh, giống như&lt;span&gt; &lt;/span&gt;&lt;a href=&quot;http://muabanruoubia.com/wiki/C%C3%B4_nh%E1%BA%AFc&quot; title=&quot;C&amp;ocirc; nhắc&quot;&gt;C&amp;ocirc; nhắc&lt;/a&gt;&lt;span&gt; &lt;/span&gt;tuy cũng c&amp;oacute; nhiều d&amp;ograve;ng kh&amp;aacute;c nhau, nhưng chủ yếu gồm 3 loại ch&amp;iacute;nh:&lt;/p&gt;\n&lt;ul&gt;\n&lt;li&gt;&lt;strong&gt;Brut&lt;/strong&gt;: rượu chua, thường d&amp;ugrave;ng khai vị.&lt;/li&gt;\n&lt;li&gt;&lt;strong&gt;Demi-sec&lt;/strong&gt;: hơi chua, d&amp;ugrave;ng khai vị hoặc d&amp;ugrave;ng suốt bữa ăn, uống ban ng&amp;agrave;y.&lt;/li&gt;\n&lt;li&gt;&lt;strong&gt;Champagne ngọt&lt;/strong&gt;: uống k&amp;egrave;m l&amp;uacute;c ăn tr&amp;aacute;ng miệng hoặc uống sau bữa ăn.&lt;/li&gt;\n&lt;/ul&gt;\n&lt;h2&gt;&lt;span style=&quot;float: right; margin-left: 5px; font-size: 13px;&quot;&gt;&lt;/span&gt;&lt;span&gt;Thưởng rượu&lt;/span&gt;&lt;/h2&gt;\n&lt;p style=&quot;margin: 0.4em 0px 0.5em; line-height: 1.5em;&quot;&gt;Ly uống rượu s&amp;acirc;m panh th&amp;iacute;ch hợp nhất l&amp;agrave; loại ly h&amp;igrave;nh hoa&lt;span&gt; &lt;/span&gt;&lt;a href=&quot;http://muabanruoubia.com/w/index.php?title=Tuy_l%C3%ADp&amp;amp;action=edit&amp;amp;redlink=1&quot; title=&quot;Tuy l&amp;iacute;p (trang chưa được viết)&quot;&gt;tuy l&amp;iacute;p&lt;/a&gt;&lt;span&gt; &lt;/span&gt;d&amp;agrave;i, v&amp;igrave; loại ly n&amp;agrave;y đủ rộng để c&amp;aacute;c bọt kh&amp;iacute; tự do nhảy m&amp;uacute;a, v&amp;agrave; m&amp;agrave;u sắc, hương vị cũng như độ trong của rượu dễ d&amp;agrave;ng được thẩm định.&lt;/p&gt;\n&lt;p style=&quot;margin: 0.4em 0px 0.5em; line-height: 1.5em;&quot;&gt;D&amp;ugrave;ng s&amp;acirc;m panh ở nhiệt độ từ 7 đến 10 &amp;deg;C l&amp;agrave; th&amp;iacute;ch hợp nhất. Do vậy, c&amp;aacute;c chai rượu khi đưa ra uống thường được ng&amp;acirc;m trong x&amp;ocirc; đ&amp;aacute; hoặc để ngăn m&amp;aacute;t&lt;span&gt; &lt;/span&gt;&lt;a href=&quot;http://muabanruoubia.com/w/index.php?title=T%E1%BB%A7_l%E1%BA%A1nh&amp;amp;action=edit&amp;amp;redlink=1&quot; title=&quot;Tủ lạnh (trang chưa được viết)&quot;&gt;tủ lạnh&lt;/a&gt;&lt;span&gt; &lt;/span&gt;trong v&amp;agrave;i giờ đồng hồ.&lt;/p&gt;', '4e4f4ae00401d200px-Champagne.jpg', 1313813643, 'Tìm hiểu về Champagne', 'Tìm hiểu về Champagne', 'Tìm hiểu về Champagne');
INSERT INTO `news_contents` (`id`, `news_category_id`, `name`, `friendly_url`, `intro_text`, `content`, `image_path`, `date_created`, `page_title`, `meta_keywords`, `meta_description`) VALUES
(9, 2, 'Sâm banh thơm hương lựu', 'sam_banh_thom_huong_luu', 'Hương quyến rũ, nồng nàn từ ly sâm banh phảng phất mùi thơm nước lựu chắc chắn sẽ đem đến cho bạn và người thương một buổi tối ngọt ngào.', '&lt;p style=&quot;border-width: 0px; margin: 15px 0px; padding: 0px; font-size: 12px; background-color: transparent; text-align: justify;&quot;&gt;&lt;strong&gt;Hương quy&amp;ecirc;́n rũ, n&amp;ocirc;̀ng nàn từ ly s&amp;acirc;m banh phảng ph&amp;acirc;́t mùi thơm nước lựu chắc chắn sẽ đem đ&amp;ecirc;́n cho bạn và người thương m&amp;ocirc;̣t bu&amp;ocirc;̉i t&amp;ocirc;́i ngọt ngào.&lt;/strong&gt;&lt;/p&gt;\n&lt;p style=&quot;border-width: 0px; margin: 15px 0px; padding: 0px; font-size: 12px; background-color: transparent; text-align: justify;&quot;&gt;&lt;strong&gt;&lt;a href=&quot;http://muabanruoubia.com/wp-content/uploads/2010/04/sam-banh-nuoc-luu1.jpg&quot;&gt;&lt;img class=&quot;aligncenter size-full wp-image-561&quot; src=&quot;http://muabanruoubia.com/wp-content/uploads/2010/04/sam-banh-nuoc-luu1.jpg&quot; border=&quot;0&quot; alt=&quot;sam-banh-nuoc-luu1&quot; width=&quot;500&quot; height=&quot;531&quot; /&gt;&lt;/a&gt;&lt;br /&gt; &lt;/strong&gt;&lt;/p&gt;\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\n&lt;p style=&quot;border-width: 0px; margin: 15px 0px; padding: 5px 0px 5px 55px ! important; font-size: 12px; background-color: transparent;&quot;&gt;&lt;span style=&quot;border-width: 0px; margin: 0px; padding: 0px; font-size: 12px; background-color: transparent; color: #993300;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;border-width: 0px; margin: 0px; padding: 0px; font-size: 12px; background-color: transparent;&quot;&gt;01&lt;/span&gt;Nguy&amp;ecirc;n li&amp;ecirc;̣u&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\n&lt;ol style=&quot;border-width: 0px; margin: 15px 0px; padding: 0px; font-size: 12px; background-color: transparent;&quot;&gt;\n&lt;li&gt;1/2 cốc nước lọc&lt;/li&gt;\n&lt;li&gt;3 thìa đường&lt;/li&gt;\n&lt;li&gt;300 ml s&amp;acirc;m banh nguy&amp;ecirc;n chất&lt;/li&gt;\n&lt;li&gt;1 cốc &lt;a href=&quot;http://muabanruoubia.com/tag/rhum/&quot; title=&quot;rhum&quot;&gt;rhum&lt;/a&gt; trắng&lt;/li&gt;\n&lt;li&gt;1 cốc nước &amp;eacute;p lựu&lt;/li&gt;\n&lt;li&gt;1 quả chanh lớn, xắt l&amp;aacute;t mỏng.&lt;/li&gt;\n&lt;li&gt;Hạt lựu&lt;/li&gt;\n&lt;li&gt;L&amp;aacute; bạc h&amp;agrave;&lt;/li&gt;\n&lt;/ol&gt;\n&lt;p style=&quot;border-width: 0px; margin: 15px 0px; padding: 5px 0px 5px 55px ! important; font-size: 12px; background-color: transparent;&quot;&gt;&lt;span style=&quot;border-width: 0px; margin: 0px; padding: 0px; font-size: 12px; background-color: transparent; color: #993300;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;border-width: 0px; margin: 0px; padding: 0px; font-size: 12px; background-color: transparent;&quot;&gt;02&lt;/span&gt;Pha chế&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\n&lt;ol style=&quot;border-width: 0px; margin: 15px 0px; padding: 0px; font-size: 12px; background-color: transparent;&quot;&gt;\n&lt;li&gt;H&amp;ograve;a đường với &amp;frac12; cốc nước, đun s&amp;ocirc;i trong 5 ph&amp;uacute;t. Để nguội.&lt;/li&gt;\n&lt;li&gt;Trộn s&amp;acirc;m banh, rượu rhum v&amp;agrave; nước &amp;eacute;p lựu trong b&amp;igrave;nh pha &lt;a href=&quot;http://muabanruoubia.com/&quot; title=&quot;rượu&quot;&gt;rượu&lt;/a&gt;. Th&amp;ecirc;m nước đường vừa kh&amp;acirc;̉u vị.&lt;/li&gt;\n&lt;li&gt;Cho l&amp;aacute;t chanh đã thái mỏng, hạt lựu v&amp;agrave; l&amp;aacute; bạc h&amp;agrave; v&amp;agrave;o b&amp;igrave;nh. Ướp ngăn lạnh.&lt;/li&gt;\n&lt;li&gt;Khi uống, r&amp;oacute;t ra ly, th&amp;ecirc;m đ&amp;aacute;.&lt;/li&gt;\n&lt;/ol&gt;', '4e4f4afc38b40sam-banh-nuoc-luu1.jpg', 1313813761, 'Sâm banh thơm hương lựu', 'Sâm banh thơm hương lựu', 'Sâm banh thơm hương lựu'),
(10, 2, 'Ruou Johnnie Walker Gold Label 18 Years Old', 'ruou_johnnie_walker_gold_label_18_years_old', 'Là tuyệt tác đánh dấu thành công của gia đình John Walker suốt 100 năm qua, Johnnie Walker Gold Label 18 với vẽ đẹp lịch lãm và hương vị thanh nhã chế tác từ những loại whisky tinh túy trên 18 năm tuổi sẽ là món quà hoàn hảo cho những đồng nghiệp và đối tác đã đóng góp những thành công mỹ mãn trong năm qua.', '&lt;div class=&quot;entry clearfix&quot;&gt;\n&lt;div class=&quot;content clearfix&quot;&gt;\n&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;color: #ff0000;&quot;&gt;Cho những ai y&amp;ecirc;u th&amp;iacute;ch sự lịch l&amp;atilde;m&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;\n&lt;p&gt;L&amp;agrave; tuyệt t&amp;aacute;c đ&amp;aacute;nh dấu th&amp;agrave;nh c&amp;ocirc;ng của gia đ&amp;igrave;nh John Walker suốt 100 năm qua, &lt;a href=&quot;http://muabanruoubia.com/tag/johnnie-walker/&quot; title=&quot;Johnnie Walker&quot;&gt;Johnnie Walker&lt;/a&gt; Gold Label 18 với vẽ đẹp lịch l&amp;atilde;m v&amp;agrave; hương vị thanh nh&amp;atilde; chế t&amp;aacute;c từ những loại &lt;a href=&quot;http://muabanruoubia.com/tag/whisky/&quot; title=&quot;whisky&quot;&gt;whisky&lt;/a&gt; tinh t&amp;uacute;y tr&amp;ecirc;n 18 năm tuổi sẽ l&amp;agrave; m&amp;oacute;n qu&amp;agrave; ho&amp;agrave;n hảo cho những đồng nghiệp v&amp;agrave; đối t&amp;aacute;c đ&amp;atilde; đ&amp;oacute;ng g&amp;oacute;p những th&amp;agrave;nh c&amp;ocirc;ng mỹ m&amp;atilde;n trong năm qua.&lt;/p&gt;\n&lt;p&gt;&lt;a href=&quot;http://muabanruoubia.com/wp-content/uploads/2010/11/johnnie-walker-gold-label-18-year-old-whisky.jpg&quot;&gt;&lt;img class=&quot;alignleft size-full wp-image-1118&quot; src=&quot;http://muabanruoubia.com/wp-content/uploads/2010/11/johnnie-walker-gold-label-18-year-old-whisky.jpg&quot; border=&quot;0&quot; alt=&quot;johnnie-walker-gold-label-18-year-old-whisky&quot; title=&quot;johnnie-walker-gold-label-18-year-old-whisky&quot; width=&quot;300&quot; height=&quot;415&quot; /&gt;&lt;/a&gt;&lt;/p&gt;\n&lt;ul&gt;\n&lt;li&gt;Xuất xứ: Scotland&lt;/li&gt;\n&lt;li&gt;Dung t&amp;iacute;ch: 750ml&lt;/li&gt;\n&lt;li&gt;Độ cồn: 40&amp;deg;&lt;/li&gt;\n&lt;/ul&gt;\n&lt;p&gt;C&amp;oacute; rất &amp;iacute;t loại whisky n&amp;agrave;o c&amp;oacute; thể mang đến cho người thưởng thức những trải nghiệm tinh tế như Johnnie Walker Gold Label. &amp;Ecirc;m dịu v&amp;agrave; thanh nh&amp;atilde; với vị kem, Gold Label tạo n&amp;ecirc;n một niềm vui th&amp;iacute;ch bất tận, một hỗn hợp đầy cảm hứng từ mạch nha Clynelish, h&amp;ograve;a quyện c&amp;ugrave;ng nước suối chảy qua v&amp;agrave;ng nguy&amp;ecirc;n chất.&lt;/p&gt;\n&lt;p&gt;&lt;a href=&quot;http://muabanruoubia.com/&quot; title=&quot;Rượu&quot;&gt;Rượu&lt;/a&gt; được pha trộn kh&amp;eacute;o l&amp;eacute;o giữa nhiều loại whisly l&amp;acirc;u năm qu&amp;yacute; hiếm được lấy cảm hứng từ những hương vị l&amp;acirc;u đời được lưu giữ bởi Alexander Walker, được sản xuất tại một sốt &amp;iacute;t những xưởng chưng cất rượu nổi tiếng nhất xứ Scốtlen với thời gian ủ từ 18 năm trở l&amp;ecirc;n.&lt;/p&gt;\n&lt;p&gt;&lt;strong&gt;Đặc điểm&lt;/strong&gt;&lt;/p&gt;\n&lt;ul&gt;\n&lt;li&gt;&lt;strong&gt;M&amp;agrave;u&lt;/strong&gt;: v&amp;agrave;ng hổ ph&amp;aacute;ch.&lt;/li&gt;\n&lt;li&gt;&lt;strong&gt;M&amp;ugrave;i&lt;/strong&gt;: hương vị tr&amp;aacute;i c&amp;acirc;y nhẹ nh&amp;agrave;ng, hương mật ong ngọt lịm, v&amp;agrave; hương nồng ấm mạnh mẽ của gỗ v&amp;agrave; kh&amp;oacute;i.&lt;/li&gt;\n&lt;li&gt;&lt;strong&gt;Vị&lt;/strong&gt;: hương vị &amp;ecirc;m dịu, thanh nh&amp;atilde; v&amp;agrave; lắng đọng với vị mật ong, quả hạch, vị cay c&amp;ugrave;ng vị kem nhẹ nh&amp;agrave;ng, &amp;ecirc;m &amp;aacute;i l&amp;agrave;m say đắm l&amp;ograve;ng người.&lt;/li&gt;\n&lt;/ul&gt;\n&lt;p&gt;&lt;strong&gt;C&amp;aacute;ch thưởng thức&lt;/strong&gt;&lt;/p&gt;\n&lt;p&gt;Những s&amp;agrave;nh rượu tr&amp;ecirc;n thế giới đ&amp;atilde; giữ lạnh Gold Label trong v&amp;ograve;ng 24g để gi&amp;uacute;p lan tỏa hương vị. Gold Label tuyệt hảo khi được ướp lạnh v&amp;agrave; thưởng thức chung với s&amp;ocirc;c&amp;ocirc;la đắng.&lt;/p&gt;\n&lt;/div&gt;\n&lt;/div&gt;', '4e4f4b1aeca73johnnie-walker-gold-label-18-year-old-whisky.jpg', 1313813842, 'Ruou Johnnie Walker Gold Label 18 Years Old', 'Ruou Johnnie Walker Gold Label 18 Years Old', 'Ruou Johnnie Walker Gold Label 18 Years Old'),
(11, 2, 'Thương hiệu rượu hàng đầu trên toàn thế giới', 'thuong_hieu_ruou_hang_dau_tren_toan_the_gioi', 'Không có một công ty chưng cất và pha chế rượu Scotch Whisky nào trên thế giới có được sự phát triển liên tục và thuận lợi như Johnnie Walker.', '&lt;p&gt;&lt;strong&gt;Kh&amp;ocirc;ng c&amp;oacute; một c&amp;ocirc;ng ty chưng cất v&amp;agrave; pha chế rượu Scotch Whisky n&amp;agrave;o tr&amp;ecirc;n thế giới c&amp;oacute; được sự ph&amp;aacute;t triển li&amp;ecirc;n tục v&amp;agrave; thuận lợi như Johnnie Walker.&lt;/strong&gt;&lt;/p&gt;\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\n&lt;p&gt;Từ một cửa hiệu nhỏ ở Kilmarnock nằm ở ph&amp;iacute;a t&amp;acirc;y Xcốt-len, c&amp;ocirc;ng ty đ&amp;atilde; ph&amp;aacute;t triển việc kinh doanh ra to&amp;agrave;n cầu, trở th&amp;agrave;nh c&amp;ocirc;ng ty kinh doanh Scotch Whisky lớn nhất thế giới v&amp;agrave; vẫn giữ đuợc vị thế đ&amp;oacute; cho đến tận ng&amp;agrave;y h&amp;ocirc;m nay.&lt;br /&gt; &lt;strong&gt; &lt;/strong&gt;&lt;strong&gt;&lt;br /&gt; &lt;/strong&gt;&lt;strong&gt;THỊ TRƯỜNG&lt;/strong&gt;&lt;/p&gt;\n&lt;p&gt;&lt;a href=&quot;http://muabanruoubia.com/wp-content/uploads/2010/04/270406_johnnie1.jpg&quot;&gt;&lt;img class=&quot;alignleft size-full wp-image-574&quot; src=&quot;http://muabanruoubia.com/wp-content/uploads/2010/04/270406_johnnie1.jpg&quot; border=&quot;0&quot; alt=&quot;270406_johnnie1&quot; width=&quot;472&quot; height=&quot;390&quot; /&gt;&lt;/a&gt;&lt;/p&gt;\n&lt;p&gt;Johnnie Walker l&amp;agrave; c&amp;ocirc;ng ty kinh doanh rượu Scotch Whisky lớn nhất h&amp;agrave;nh tinh, c&amp;oacute; mặt tr&amp;ecirc;n 200 thị trường thế giới.&lt;/p&gt;\n&lt;p&gt;Tại &amp;Uacute;c, Johnnie Walker nh&amp;atilde;n đỏ l&amp;agrave; thương hiệu rượu mạnh đứng h&amp;agrave;ng thứ 3 v&amp;agrave; l&amp;agrave; thương hiệu &lt;a href=&quot;http://muabanruoubia.com/tag/ruou-whisky/&quot; title=&quot;rượu whisky&quot;&gt;rượu whisky&lt;/a&gt; Scốt-len h&amp;agrave;ng đầu tr&amp;ecirc;n thị trường.&lt;/p&gt;\n&lt;p&gt;&lt;strong&gt;TH&amp;Agrave;NH T&amp;Iacute;CH ĐẠT ĐƯỢC&lt;/strong&gt;&lt;/p&gt;\n&lt;p&gt;Johnnie Walker nh&amp;atilde;n đỏ Red Label&lt;/p&gt;\n&lt;p&gt;Năm 1933, khi việc kinh doanh rượu nh&amp;atilde;n đỏ Red Label của Johnnie Walker đang tr&amp;ecirc;n đ&amp;agrave; ph&amp;aacute;t triển th&amp;igrave; c&amp;ocirc;ng ty nhận được giấy ph&amp;eacute;p cung cấp rượu Scotch Whisky cho vua George V v&amp;agrave; kể từ đ&amp;oacute; c&amp;ocirc;ng ty trở th&amp;agrave;nh nh&amp;agrave; cung cấp rượu Scotch Whisky ch&amp;iacute;nh thức cho gia đ&amp;igrave;nh Ho&amp;agrave;ng Gia Anh Quốc.&lt;/p&gt;\n&lt;p&gt;Tại cuộc thi rượu mạnh v&amp;agrave; &lt;a href=&quot;http://muabanruoubia.com/cat/ruou-ngoai/ruou_vang/&quot; title=&quot;rượu vang&quot;&gt;rượu vang&lt;/a&gt; quốc tế, Johnnie Walker đ&amp;atilde; đoạt được một số huy chương v&amp;agrave;ng d&amp;agrave;nh cho hạng mục rượu Scotch Whisky. Thương hiệu Johnnie Walker cũng nhận được huy chương v&amp;agrave;ng v&amp;agrave; huy chuơng đặc biệt&amp;nbsp; tại cuộc thi Selection Awards thế giới.&lt;/p&gt;\n&lt;p&gt;Năm 1996, IWSC tặng cho Johnnie Walker danh hiệu &amp;ldquo;Nh&amp;agrave; chưng cất rượu h&amp;agrave;ng đầu thế giới&amp;rdquo;.&lt;/p&gt;\n&lt;p&gt;Johnnie Walker nh&amp;atilde;n đen Black Label&lt;/p&gt;\n&lt;p&gt;Tại cuộc thi Selection Awards thế giới năm 1994, Black Label gi&amp;agrave;nh được huy chương đặc biệt v&amp;agrave; c&amp;ugrave;ng năm đ&amp;oacute;, nh&amp;atilde;n hiệu n&amp;agrave;y cũng được IWSC tặng huy chuơng v&amp;agrave;ng đồng thời gi&amp;agrave;nh lu&amp;ocirc;n danh hiệu nh&amp;agrave; chưng cất rượu ngon nhất d&amp;agrave;nh cho rượu Blended Whisky (whisky đ&amp;atilde; được pha chế). Đến năm 1996, Black Label lại đoạt được huy chương v&amp;agrave;ng d&amp;agrave;nh cho rượu cao cấp Best Deluxe Scotch Whisky.&lt;/p&gt;\n&lt;p&gt;&lt;strong&gt;LỊCH SỬ H&amp;Igrave;NH TH&amp;Agrave;NH&lt;/strong&gt;&lt;/p&gt;\n&lt;p&gt;Từ một cửa h&amp;agrave;ng tạp ho&amp;aacute; nhỏ ở phố người Xcốt-len thuộc Kilmarnock, chỉ trong v&amp;ograve;ng khoảng hơn 175 năm, c&amp;ocirc;ng ty đ&amp;atilde; ph&amp;aacute;t triển việc kinh doanh của m&amp;igrave;nh ra to&amp;agrave;n thế giới. Việc th&amp;agrave;nh lập v&amp;agrave; ph&amp;aacute;t triển c&amp;ocirc;ng ty được đặt ra khi John Walker &amp;ndash; cậu chủ trẻ tuổi của cửa hiệu &amp;ndash; bắt đầu thử nghiệm pha trộn c&amp;aacute;c loại rượu Whisky c&amp;oacute; nguồn gốc từ ng&amp;ocirc; với mạch nha trong ph&amp;ograve;ng tối.&lt;/p&gt;\n&lt;p&gt;Kh&amp;ocirc;ng c&amp;oacute; một c&amp;ocirc;ng ty chưng cất v&amp;agrave; pha chế rượu Scotch Whisky n&amp;agrave;o tr&amp;ecirc;n thế giới c&amp;oacute; được sự ph&amp;aacute;t triển li&amp;ecirc;n tục v&amp;agrave; thuận lợi như Johnnie Walker. Từ một cửa hiệu nhỏ ở Kilmarnock nằm ở ph&amp;iacute;a t&amp;acirc;y Xcốt-len, c&amp;ocirc;ng ty đ&amp;atilde; ph&amp;aacute;t triển việc kinh doanh ra to&amp;agrave;n cầu, trở th&amp;agrave;nh c&amp;ocirc;ng ty kinh doanh Scotch Whisky lớn nhất thế giới v&amp;agrave; c&amp;ocirc;ng ty vẫn giữ đuợc vị thế đ&amp;oacute; cho đến tận ng&amp;agrave;y h&amp;ocirc;m nay. Johnnie Walker Red Label l&amp;agrave; thuơng hiệu đầu ti&amp;ecirc;n được ph&amp;aacute;t triển ra khắp to&amp;agrave;n cầu, c&amp;oacute; mặt tr&amp;ecirc;n 120 thị trường thế giới trong chiến tranh thế giới lần 1.&lt;/p&gt;\n&lt;p&gt;George and Alexander Walker đ&amp;atilde; nhận ra cơ hội ph&amp;aacute;t triển thương hiệu trong thị trường rượu scotch whisky thế giới. George Paterson Walker, nguời chịu tr&amp;aacute;ch nhiệm tiếp thị v&amp;agrave; anh trai của &amp;ocirc;ng Alexander, nh&amp;agrave; thi&amp;ecirc;n t&amp;agrave;i trong việc pha chế rượu, đ&amp;atilde; nhận ra rằng những loại rượu đ&amp;atilde; được pha chế trước đ&amp;oacute; điều qu&amp;aacute; nặng v&amp;agrave; kh&amp;oacute; uống, kh&amp;oacute; c&amp;oacute; thể ưa chuộng rộng r&amp;atilde;i, cho n&amp;ecirc;n thị hiếu thị truờng cần c&amp;oacute; một loại rượu Whisky nhẹ hơn d&amp;agrave;nh cho những nguời vừa mới tập l&amp;agrave;m quen Whisky.&lt;/p&gt;\n&lt;p&gt;V&amp;agrave; kết quả l&amp;agrave; Johnnie Walker Red Label ra đời năm 1909. Từ loại rượu Whisky Old Highland được y&amp;ecirc;u th&amp;iacute;ch trong thời cha của &amp;ocirc;ng (được tung ra v&amp;agrave;o năm 1820), Alexander Walker đ&amp;atilde; ph&amp;aacute;t triển th&amp;agrave;nh những loại rượu&amp;nbsp; Whisky c&amp;oacute; nồng độ v&amp;agrave; hương vị rất đa dạng, phong ph&amp;uacute;. Đ&amp;acirc;y ch&amp;iacute;nh l&amp;agrave; rượu&amp;nbsp; Whisky d&amp;agrave;nh cho thế kỷ mới, được tiếp thị rộng r&amp;atilde;i tr&amp;ecirc;n to&amp;agrave;n thế giới. Lần đầu ti&amp;ecirc;n một loại rượu mang thương hiệu &amp;ldquo;Johnnie Walker&amp;rdquo; ra đời. Họ đặt t&amp;ecirc;n cho loại rượu pha mới l&amp;agrave; Johnnie Walker Red Label v&amp;agrave; n&amp;oacute; nhanh ch&amp;oacute;ng trở th&amp;agrave;nh loại whisky được y&amp;ecirc;u chuộng tr&amp;ecirc;n khắp thế giới.&lt;/p&gt;\n&lt;p&gt;Những loại rượu whisky của Johnnie Walker với những c&amp;ocirc;ng thức pha chế đặc biệt đ&amp;atilde; được đ&amp;oacute;n nhận v&amp;agrave; đ&amp;atilde; gi&amp;agrave;nh được rất nhiều huy chương, giải thưởng tại những cuộc thi quốc tế trong hơn 120 năm qua. C&amp;ocirc;ng ty cũng tự h&amp;agrave;o l&amp;agrave; nh&amp;agrave; cung cấp rượu whisky cho ho&amp;agrave;ng gia Anh từ năm 1933 đến giờ. H&amp;igrave;nh ảnh biểu tượng của Johnnie Walker Striding Man (người đ&amp;agrave;n &amp;ocirc;ng đang bước về ph&amp;iacute;a trước với những bước đi d&amp;agrave;i) được hoạ sĩ biếm Tom Browne vẽ v&amp;agrave;o năm 1909 đ&amp;atilde; trở th&amp;agrave;nh một h&amp;igrave;nh ảnh tuyệt vời được to&amp;agrave;n thế giới c&amp;ocirc;ng nhận v&amp;agrave; l&amp;agrave; hiện th&amp;acirc;n của loại rượu mạnh d&amp;agrave;nh cho cả thế giới.&lt;/p&gt;\n&lt;p&gt;Khi những nh&amp;atilde;n hiệu mới ra đời năm 1909, anh em nh&amp;agrave; Walker quyết định tạo ra một bộ mặt mới cho thương hiệu&amp;nbsp; Walker.&lt;/p&gt;\n&lt;p&gt;Năm 1920, Walker trở th&amp;agrave;nh một trong 3 c&amp;ocirc;ng ty whisky h&amp;agrave;ng đầu tr&amp;ecirc;n thế giới đặc biệt l&amp;agrave; Red Label. V&amp;agrave; c&amp;ugrave;ng với Red Label, Black Label cũng được xuất khẩu sang thị trường Hoa Kỳ.&lt;/p&gt;\n&lt;p&gt;Nh&amp;atilde;n hiệu&amp;nbsp; Johnnie Walker Black Label đ&amp;atilde; được ra đời c&amp;aacute;ch đ&amp;acirc;y hơn 125 năm. Để trở th&amp;agrave;nh một c&amp;ocirc;ng ty nổi tiếng v&amp;agrave; lu&amp;ocirc;n giữ được vị thế của m&amp;igrave;nh qua thời gian, người s&amp;aacute;ng lập chắc hẳn phải l&amp;agrave; một người rất ki&amp;ecirc;n định. Johnnie Walker Black Label được th&amp;agrave;nh lập năm 1920 bởi Alexander Walker I. Triết l&amp;yacute; của &amp;ocirc;ng nằm trong c&amp;acirc;u n&amp;oacute;i &amp;ldquo;to make our whisky of such quality that nothing in the market shall come before it.&amp;rdquo; (l&amp;agrave;m ra rượu whisky chất lượng m&amp;agrave; kh&amp;ocirc;ng c&amp;oacute; loại rượu n&amp;agrave;o kh&amp;aacute;c c&amp;oacute; thể s&amp;aacute;nh kịp).&lt;/p&gt;\n&lt;p&gt;&amp;Uacute;c l&amp;agrave; một trong những thị trường đầu ti&amp;ecirc;n v&amp;agrave; lớn nhất ở nước ngo&amp;agrave;i của Johnnie Walker. Ch&amp;iacute;nh Alexander Walker l&amp;agrave; người đầu ti&amp;ecirc;n mang Scotch Whisky&amp;nbsp; đến &amp;Uacute;c bằng t&amp;agrave;u thuỷ. Năm 1867, &amp;ocirc;ng đăng k&amp;yacute; độc quyền thương hiệu Old Highland Whisky v&amp;agrave; n&amp;oacute; nhanh ch&amp;oacute;ng trở quen thuộc ở cả những nơi xa x&amp;ocirc;i nhất của nước Anh. Thươnghiệu n&amp;agrave;y c&amp;oacute; &amp;yacute; nghĩa đặc biệt khi nhắc nhở cho những kiều b&amp;agrave;o Scot ở &amp;Uacute;c nhớ về qu&amp;ecirc; hương xứ sở của m&amp;igrave;nh.&lt;/p&gt;\n&lt;p&gt;Để giữ vững mối quan hệ l&amp;acirc;u d&amp;agrave;i với thị trường &amp;Uacute;c, Johnnie Walker lu&amp;ocirc;n t&amp;igrave;m c&amp;aacute;ch đ&amp;aacute;p ứng nhu cầu của kh&amp;aacute;ch h&amp;agrave;ng v&amp;igrave; vậy cũng kh&amp;ocirc;ng c&amp;oacute; g&amp;igrave; đ&amp;aacute;ng ngạc nhi&amp;ecirc;n khi người d&amp;acirc;n &amp;Uacute;c bảo rằng rượu Whisky ưa th&amp;iacute;ch của họ ch&amp;iacute;nh l&amp;agrave; Johnnie Walker. Đ&amp;oacute; l&amp;agrave; l&amp;yacute; do tại sao&amp;nbsp; Johnnie Walker Scotch Whisky ng&amp;agrave;y nay vừa c&amp;oacute; hương vị truyền thống của Red Label vừa c&amp;oacute; hương vị cola tuyệt hảo v&amp;agrave; đuợc đ&amp;oacute;ng trong lon hoặc chai.&lt;/p&gt;\n&lt;p&gt;&lt;strong&gt;SẢN PHẨM&lt;/strong&gt;&lt;strong&gt;&lt;br /&gt; &lt;/strong&gt;&lt;br /&gt; Johnnie Walker Red Label được cả thế giới c&amp;ocirc;ng nhận l&amp;agrave; nh&amp;atilde;n hiệu scotch whisky cổ điển c&amp;oacute; gi&amp;aacute; trị cao với chai h&amp;igrave;nh vu&amp;ocirc;ng. H&amp;igrave;nh ảnh Striding Man cũng được c&amp;ocirc;ng nhận l&amp;agrave; biểu tuợng về chất lượng của Scotch Whisky tr&amp;ecirc;n to&amp;agrave;n thế giới.&lt;/p&gt;\n&lt;p&gt;Tr&amp;ecirc;n thế giới, thương hiệu Johnnie Walker được giới chuy&amp;ecirc;n m&amp;ocirc;n thừa nhận l&amp;agrave; loại rượu c&amp;oacute; chất lượng tốt nhất.&lt;/p&gt;\n&lt;p&gt;Khi người ti&amp;ecirc;u d&amp;ugrave;ng ở ch&amp;acirc;u &amp;Acirc;u, Mỹ v&amp;agrave; Nhật d&amp;ugrave;ng thử những loại whisky đ&amp;atilde; được b&amp;oacute;c nh&amp;atilde;n, rượu của Johnnie Walker đuợc người ti&amp;ecirc;u d&amp;ugrave;ng y&amp;ecirc;u chuộng hơn hẳn những đối thủ cạnh tranh bởi hương vị đặc trưng của n&amp;oacute;. Whisky mạch nha Cardhu l&amp;agrave; sản phẩm chủ lực trong d&amp;ograve;ng whisky pha Red Label mang hương mạch nha, ngọt ng&amp;agrave;o, dễ uống. Anh em nh&amp;agrave; Walker&amp;nbsp; coi đ&amp;acirc;y l&amp;agrave; sản phẩn quan trọng trong việc đ&amp;aacute;p ứng nhu cầu của người ti&amp;ecirc;u d&amp;ugrave;ng v&amp;agrave; họ đ&amp;atilde; mua nh&amp;agrave; m&amp;aacute;y chưng cất loại rượu n&amp;agrave;y năm1893.&lt;/p&gt;\n&lt;p&gt;Johnnie Walker Black Label l&amp;agrave; nh&amp;atilde;n hiệu Whisky cao cấp rất được y&amp;ecirc;u th&amp;iacute;ch. Những loại whisky mang nh&amp;atilde;n Johnnie Walker Black Label đều c&amp;oacute; thể được bảo đảm c&amp;oacute; tuổi thọ &amp;iacute;t nhất 12 năm thậm ch&amp;iacute; l&amp;acirc;u hơn. Điều đ&amp;oacute; c&amp;oacute; nghĩa l&amp;agrave; để tạo ra được loại rượu tuyệt hảo n&amp;agrave;y c&amp;ocirc;ng ty đ&amp;atilde; phải mất gần 2 thập kỷ, một kế hoạch chu to&amp;agrave;n.&lt;/p&gt;\n&lt;p&gt;Pha chế kh&amp;ocirc;ng phải l&amp;agrave; ng&amp;agrave;nh khoa học đ&amp;ograve;i hỏi t&amp;iacute;nh ch&amp;iacute;nh x&amp;aacute;c m&amp;agrave; &amp;agrave; một nghệ thuật. N&amp;oacute; đ&amp;ograve;i hỏi người pha chế phải c&amp;oacute; một tr&amp;iacute; nhớ tuyệt vời về hương vị của c&amp;aacute;c loại whisky m&amp;agrave; anh ta định sử dụng v&amp;igrave; thế t&amp;agrave;i sản lớn nhất của người pha chế ch&amp;iacute;nh l&amp;agrave; c&amp;aacute;i mũi. Mỗi loại whisky phải được ngửi &amp;iacute;t nhất 6 lần, bắt đầu từ trước khi n&amp;oacute; được cho v&amp;agrave;o th&amp;ugrave;ng chưng cất đến l&amp;uacute;c kết th&amp;uacute;c khi sản phẩm pha chế được đ&amp;oacute;&lt;/p&gt;\n&lt;p&gt;&lt;strong&gt;NHỮNG PH&amp;Aacute;T TRIỂN GẦN Đ&amp;Acirc;Y&lt;/strong&gt;&lt;/p&gt;\n&lt;p&gt;Hai hương vị được y&amp;ecirc;u th&amp;iacute;ch nhất tr&amp;ecirc;n thế giới vừa được pha trộn lại với nhau để tạo ra một loại thức uống c&amp;oacute; hương vị tươi m&amp;aacute;t v&amp;agrave; tăng cường sức khỏe. Đ&amp;oacute; l&amp;agrave; Johnnie Walker Red Label &amp;amp; Cola. Nồng độ đặc biệt v&amp;agrave; hương vị tuyệt vời được bổ sung bởi loại cola kh&amp;ocirc; thượng hạng v&amp;agrave; kết quả đ&amp;atilde; tạo ra được&amp;nbsp; một loại whisky thơm hơn ngon hơn tất cả những sản phẩm whisky đ&amp;atilde; được pha chế trước kia.&lt;/p&gt;\n&lt;p&gt;[tag]&lt;br /&gt; &lt;strong&gt;KHUYẾN THỊ&lt;/strong&gt;&lt;/p&gt;\n&lt;p&gt;Johnnie Walker Black Label v&amp;agrave; m&amp;ocirc;n thể thao Golf c&amp;ugrave;ng bắt tay hợp t&amp;aacute;c với nhau v&amp;agrave; địa h&amp;igrave;nh n&amp;uacute;i kh&amp;ocirc;ng chỉ l&amp;agrave;m cho Highlands trở th&amp;agrave;nh nơi l&amp;yacute; tưởng để tạo n&amp;ecirc;n những s&amp;acirc;n golf m&amp;agrave; nơi đ&amp;acirc;y c&amp;ograve;n c&amp;oacute; những trận mưa r&amp;agrave;o cung cấp cho Scotch Whisky nguồn nước tinh khiết.&lt;/p&gt;\n&lt;p&gt;Mối quan hệ dần được h&amp;igrave;nh th&amp;agrave;nh giữa golf v&amp;agrave; Scotch. Alexander Walker v&amp;agrave; con trai &amp;ocirc;ng, ng&amp;agrave;i Alexander đều l&amp;agrave; những người y&amp;ecirc;u th&amp;iacute;ch golf. Họ chơi ở c&amp;acirc;u lạc bộ golf ho&amp;agrave;ng gia Troon nằm c&amp;aacute;ch nh&amp;agrave; chưa đầy 50 m&amp;eacute;t, nơi Alexander Walker tiếp đ&amp;oacute;n những tay golf cừ kh&amp;ocirc;i nhất. Ch&amp;iacute;nh sự nhiệt t&amp;igrave;nh n&amp;agrave;y đ&amp;atilde; dẫn đến mối quan hệ giữa Black Label với golf v&amp;agrave; ng&amp;agrave;y nay, Black Label trở th&amp;agrave;nh nh&amp;agrave; t&amp;agrave;i trợ cho m&amp;ocirc;n thể thao n&amp;agrave;y tr&amp;ecirc;n khắp thế giới cả giải nghiệp dư lẫn chuy&amp;ecirc;n nghiệp.&lt;/p&gt;\n&lt;p&gt;Trong PGA European Tour v&amp;agrave; Johnnie Walker Classic được tổ chức ở Ch&amp;acirc;u &amp;Aacute;, những vận động vi&amp;ecirc;n lu&amp;ocirc;n lu&amp;ocirc;n nỗ lực để đạt được th&amp;agrave;nh t&amp;iacute;ch mới, gi&amp;agrave;nh được nhữg giải thuởng như Johnnie Waker Tour Course Record Award, Johnnie Walker Player h&amp;agrave;ng th&amp;aacute;ng v&amp;agrave; giải đấu của năm.&lt;/p&gt;\n&lt;p&gt;Người ta kể rằng trong những ng&amp;agrave;y lạnh đầy gi&amp;oacute;, Alexander Walker rất th&amp;ocirc;ng cảm với những người chơi golf v&amp;agrave; khuyến kh&amp;iacute;ch họ ho&amp;agrave;n th&amp;agrave;nh 18 lỗ golf bằng 1 ly whisky Scotch ấm &amp;aacute;p ở cuối buổi tập. Ng&amp;agrave;y nay, truyền thống n&amp;agrave;y được duy tr&amp;igrave; ở nơi&amp;nbsp; m&amp;agrave; những tay golf gọi n&amp;ocirc;m na l&amp;agrave; &amp;ldquo; lỗ thứ 19&amp;rdquo; &amp;ndash; quầy rượu của c&amp;acirc;u lạc bộ, nơi d&amp;agrave;nh cho những người bạn c&amp;ugrave;ng thưởng thức Johnnie Walker Black Label.&lt;/p&gt;\n&lt;p&gt;&lt;strong&gt;GI&amp;Aacute; TRỊ THƯƠNG HIỆU&lt;/strong&gt;&lt;strong&gt;&lt;br /&gt; &lt;/strong&gt;&lt;br /&gt; Johnnie Walker Red Label l&amp;agrave; loại whisky b&amp;aacute;n chạy nhất thị trường &amp;Uacute;c v&amp;agrave; l&amp;agrave; thương hiệu rượu mạnh đứng vị tr&amp;iacute; thứ 3 tr&amp;ecirc;n thế giới. Nh&amp;atilde;n hiệu v&amp;agrave; biểu tượng Striding Man được c&amp;ocirc;ng nhận l&amp;agrave; những biểu tượng nổi tiếng tr&amp;ecirc;n to&amp;agrave;n cầu v&amp;agrave; được đăng k&amp;yacute; sở hữu tr&amp;ecirc;n thị trường trước cả McDonald&amp;rsquo;s, Nike v&amp;agrave; Coca-Cola.&lt;/p&gt;\n&lt;p&gt;Những d&amp;ograve;ng sản phẩm whisky của gia đ&amp;igrave;nh Johnnie Walker c&amp;ugrave;ng nhau chia sẻ gi&amp;aacute; trị của sự ph&amp;aacute;t triển, tiếp tục đứng vững v&amp;agrave; ng&amp;agrave;y c&amp;agrave;ng lớn mạnh. Những gi&amp;aacute; trị đ&amp;oacute; được mang đến bởi những điều cốt l&amp;otilde;i như kiểu d&amp;aacute;ng sản phẩm, thương hiệu Johnnie Walker, v&amp;agrave; biểu tượng Striding Man. Ch&amp;iacute;nh những điều đ&amp;oacute; cũng g&amp;oacute;p phần kh&amp;ocirc;ng nhỏ trong việc gi&amp;uacute;p thương hiệu Johnnie Walker giữ vững được vị tr&amp;iacute; h&amp;agrave;ng đầu tr&amp;ecirc;n to&amp;agrave;n thế giới.&lt;/p&gt;', '4e4f4b301846e270406_johnnie1.jpg', 1313813928, 'Thương hiệu rượu hàng đầu trên toàn thế giới', 'Thương hiệu rượu hàng đầu trên toàn thế giới', 'Thương hiệu rượu hàng đầu trên toàn thế giới'),
(12, 2, 'HENNESSY V.S.O.P', 'hennessy_vsop', 'Năm 1817, Đức Vua tương lai George IV của nước Anh (khi đó chưa lên ngôi) đã đề nghị Hennessy cung cấp cho Ngài một loại rượu mà ông mô tả theo đặt hàng là “Very Superior, Old and Pale cognac” và thế là Hennessy đã sản xuất loại rượu viết tắt theo đặt hàng của Hoàng Gia Anh là “V.S.O.P.” (V = Very, S = Superior, O = Old, P = Pale). Đến thế kỷ 19, nhãn hiệu Hennessy V.S.O.P. pha chế với khoảng 60 loại rượu nho “Aux-de-vie” cao tuổi tối thiểu 15 năm và chỉ dùng nho của 4 vườn nho hàng đầu trong khu vực Cognac mà thôi.', '&lt;div class=&quot;entry clearfix&quot;&gt;\n&lt;div class=&quot;content clearfix&quot;&gt;\n&lt;p&gt;* Xuất xứ: Ph&amp;aacute;p&lt;br /&gt; * Dung t&amp;iacute;ch: 700ml&lt;br /&gt; * Độ cồn: 40&amp;deg;&lt;/p&gt;\n&lt;p&gt;&lt;img class=&quot;aligncenter size-full wp-image-288&quot; src=&quot;http://muabanruoubia.com/wp-content/uploads/2010/03/co_02hennessyvsop_2434284.jpg&quot; border=&quot;0&quot; alt=&quot;Print&quot; title=&quot;Print&quot; width=&quot;700&quot; height=&quot;700&quot; /&gt;&lt;/p&gt;\n&lt;p&gt;Năm 1817, Đức Vua tương lai George IV của nước Anh (khi đ&amp;oacute; chưa l&amp;ecirc;n ng&amp;ocirc;i) đ&amp;atilde; đề nghị &lt;a href=&quot;http://muabanruoubia.com/2010/hennessy/&quot; title=&quot;Hennessy&quot;&gt;Hennessy&lt;/a&gt; cung cấp cho Ng&amp;agrave;i một loại &lt;a href=&quot;http://muabanruoubia.com/&quot; title=&quot;rượu&quot;&gt;rượu&lt;/a&gt; m&amp;agrave; &amp;ocirc;ng m&amp;ocirc; tả theo đặt h&amp;agrave;ng l&amp;agrave; &amp;ldquo;Very Superior, Old and Pale cognac&amp;rdquo; v&amp;agrave; thế l&amp;agrave; Hennessy đ&amp;atilde; sản xuất loại rượu viết tắt theo đặt h&amp;agrave;ng của Ho&amp;agrave;ng Gia Anh l&amp;agrave; &amp;ldquo;V.S.O.P.&amp;rdquo; (V = Very, S = Superior, O = Old, P = Pale). Đến thế kỷ 19, nh&amp;atilde;n hiệu Hennessy V.S.O.P. pha chế với khoảng 60 loại &lt;a href=&quot;http://muabanruoubia.com/2010/ruou-nho/&quot; title=&quot;rượu nho&quot;&gt;rượu nho&lt;/a&gt; &amp;ldquo;Aux-de-vie&amp;rdquo; cao tuổi tối thiểu 15 năm v&amp;agrave; chỉ d&amp;ugrave;ng nho của 4 vườn nho h&amp;agrave;ng đầu trong khu vực Cognac m&amp;agrave; th&amp;ocirc;i.&lt;br /&gt; Hennessy VSOP (Very Superior Old Pale) l&amp;agrave; sự pha trộn tổng hợp của hơn 60 loại eau-de-vie kh&amp;aacute;c nhau được ủ trong những th&amp;ugrave;ng gỗ sồi l&amp;acirc;u năm. Hennessy VSOP c&amp;oacute; m&amp;agrave;u sắc v&amp;agrave;ng n&amp;acirc;u, l&amp;agrave; sự pha trộn của vị gỗ sồi v&amp;agrave; vị cay. B&amp;ecirc;n cạnh đ&amp;oacute;, người uống c&amp;ograve;n c&amp;oacute; thể cảm nhận được hương vị của m&amp;ugrave;i mật ong v&amp;agrave; cam thảo.&lt;/p&gt;\n&lt;/div&gt;\n&lt;/div&gt;', '4e4f4b52d973eco_02hennessyvsop_2434284.jpg', 1313814104, 'HENNESSY V.S.O.P', 'HENNESSY V.S.O.P', 'HENNESSY V.S.O.P'),
(13, 2, 'Lịch sử Vodka', 'lich_su_vodka', 'Vodka la thứ đồ uống tồn tại song song cùng với lịch sử nhân loại.sẽ tốt hơn nếu bạn tìm hiểu về vodka.\nVodka được làm ra ở đâu và khi nào? Nó được làm từ nguyên liệu gì? trước đây và bây giờ vodka đã được sử dụng ra sao?', '&lt;div class=&quot;entry clearfix&quot;&gt;\n&lt;div class=&quot;content clearfix&quot;&gt;\n&lt;p&gt;&lt;a href=&quot;http://muabanruoubia.com/2010/vodka/&quot; title=&quot;Vodka&quot;&gt;Vodka&lt;/a&gt; la thứ đồ uống tồn tại song song c&amp;ugrave;ng với lịch sử nh&amp;acirc;n loại.sẽ tốt hơn nếu bạn t&amp;igrave;m hiểu về &lt;a href=&quot;http://muabanruoubia.com/2010/vodka-2/&quot; title=&quot;vodka&quot;&gt;vodka&lt;/a&gt;.&lt;br /&gt; Vodka được l&amp;agrave;m ra ở đ&amp;acirc;u v&amp;agrave; khi n&amp;agrave;o? N&amp;oacute; được l&amp;agrave;m từ nguy&amp;ecirc;n liệu g&amp;igrave;? trước đ&amp;acirc;y v&amp;agrave; b&amp;acirc;y giờ vodka đ&amp;atilde; được sử dụng ra sao?&lt;br /&gt; Vodka được n&amp;oacute;i đến với rất nhiều quan điểm kh&amp;aacute;c nhau. Một v&amp;agrave;i quan điểm cho rằng n&amp;oacute; l&amp;agrave; thi&amp;ecirc;n đường, số kh&amp;aacute;c lai cho n&amp;oacute; l&amp;agrave; địa ngục. Cả hai quan điểm tr&amp;ecirc;n đều kh&amp;oacute; ph&amp;acirc;n biệt đ&amp;uacute;ng sai.&lt;br /&gt; Người nga c&amp;oacute; truyền thống uống vodka trong c&amp;aacute;c ng&amp;agrave;y lễ hội, ng&amp;agrave;y kỷ niệm như: Tiệc cưới, sinh nhật hay khi t&amp;igrave;m được c&amp;ocirc;ng việc mới&amp;hellip;&lt;br /&gt; Sự hiện diện của n&amp;oacute; kh&amp;ocirc;ng c&amp;oacute; g&amp;igrave; l&amp;agrave; nghi&amp;ecirc;m trọng cả. N&amp;oacute; giống như một loại nước uống th&amp;ocirc;ng thường &amp;ldquo;ở nga từ &amp;ldquo; vodka&amp;rdquo; chỉ co nghĩa l&amp;agrave; một ch&amp;uacute;t nước v&amp;agrave; người ta kh&amp;ocirc;ng c&amp;oacute; &amp;yacute; nghĩ về mặt ti&amp;ecirc;u cực của vodka. Thực ra vodka cũng giống như bất cứ một loại đồ uống c&amp;oacute; cồn n&amp;agrave;o n&amp;oacute; cũng c&amp;oacute; những mặt t&amp;iacute;ch cực của n&amp;oacute;.&lt;br /&gt; C&amp;oacute; nhiều lời đồn đại về nguồn gốc của vodka. Cũng giống như &lt;a href=&quot;http://muabanruoubia.com/&quot; title=&quot;rượu&quot;&gt;rượu&lt;/a&gt; vang đ&amp;atilde; được con người sử dụng h&amp;agrave;ng ng&amp;agrave;n năm nay.&lt;br /&gt; Một điều chắc chắn rằng vodka được l&amp;agrave;m ra đầu ti&amp;ecirc;n ở Đ&amp;ocirc;ng &amp;acirc;u, nơi c&amp;oacute; nhiều c&amp;aacute;c tu sĩ, thầy lang đ&amp;atilde; sử dụng cồn để hoa tan c&amp;aacute;c loại dược liệu từ thảo mộc l&amp;agrave;m th&amp;agrave;nh toa thuốc để chũa trị cho nhiều loại bệnh bởi cồn c&amp;oacute; thể ho&amp;agrave; tan tốt hơn nước rất nhiều v&amp;agrave; n&amp;oacute; c&amp;ograve;n c&amp;oacute; thể chống nhiễm tr&amp;ugrave;ng,&lt;br /&gt; V&amp;igrave; vậy c&amp;aacute;c loại thuốc được ho&amp;agrave; tan từ thảo mộc đ&amp;atilde; được sử dụng rộng r&amp;atilde;i giống như một c&amp;aacute;ch chữa bệnh mới v&amp;agrave; cồn cũng đ&amp;atilde; được sử dụng suốt trong giai đoạn n&amp;agrave;y.&lt;br /&gt; Ng&amp;agrave;y nay, cồn vẫn c&amp;ograve;n được sử dụng rộng r&amp;atilde;i như một lại thuốc.&lt;/p&gt;\n&lt;p&gt;&lt;a href=&quot;http://muabanruoubia.com/wp-content/uploads/2010/05/image0011.jpg&quot;&gt;&lt;img class=&quot;aligncenter size-full wp-image-794&quot; src=&quot;http://muabanruoubia.com/wp-content/uploads/2010/05/image0011.jpg&quot; border=&quot;0&quot; alt=&quot;image001&quot; width=&quot;301&quot; height=&quot;301&quot; /&gt;&lt;/a&gt;&lt;/p&gt;\n&lt;/div&gt;\n&lt;/div&gt;', '4e4f4b6aa928bimage0011.jpg', 1313814219, 'Lịch sử Vodka', 'Lịch sử Vodka', 'Lịch sử Vodka'),
(14, 2, 'Các loại Vodka chính', 'cac_loai_vodka_chinh', 'Russky Standart: Dòng Vodka theo tiêu chuẩn hoàng gia, được tinh cất để dâng cho Sa hoàng và được thiếp lập bởi chính Sa hoàng Alexander III vào năm 1894. Rượu Vodka Russky Standart khởi nguồn từ những cánh đồng lúa mạch vàng óng và nước sông băng tinh khiết của miền Bắc lạnh giá nước Nga.', '&lt;p&gt;&lt;a href=&quot;http://muabanruoubia.com/wp-content/uploads/2010/05/Moskovskaya.jpg&quot;&gt;&lt;img class=&quot;aligncenter size-full wp-image-801&quot; src=&quot;http://muabanruoubia.com/wp-content/uploads/2010/05/Moskovskaya.jpg&quot; border=&quot;0&quot; alt=&quot;Moskovskaya&quot; width=&quot;500&quot; height=&quot;500&quot; /&gt;&lt;/a&gt;Russky Standart: D&amp;ograve;ng &lt;a href=&quot;http://muabanruoubia.com/2010/vodka/&quot; title=&quot;Vodka&quot;&gt;Vodka&lt;/a&gt; theo ti&amp;ecirc;u chuẩn ho&amp;agrave;ng gia, được tinh cất để d&amp;acirc;ng cho Sa ho&amp;agrave;ng v&amp;agrave; được thiếp lập bởi ch&amp;iacute;nh Sa ho&amp;agrave;ng Alexander III v&amp;agrave;o năm 1894. &lt;a href=&quot;http://muabanruoubia.com/&quot; title=&quot;Rượu&quot;&gt;Rượu&lt;/a&gt; &lt;a href=&quot;http://muabanruoubia.com/2010/vodka-2/&quot; title=&quot;Vodka&quot;&gt;Vodka&lt;/a&gt; Russky Standart khởi nguồn từ những c&amp;aacute;nh đồng l&amp;uacute;a mạch v&amp;agrave;ng &amp;oacute;ng v&amp;agrave; nước s&amp;ocirc;ng băng tinh khiết của miền Bắc lạnh gi&amp;aacute; nước Nga.&lt;br /&gt; - Putinka Classic: Rượu Vodka mang t&amp;acirc;m hồn nước Nga. Với hương vị &amp;ecirc;m &amp;aacute;i, mềm mại, kh&amp;ocirc;ng một ch&amp;uacute;t m&amp;ugrave;i cồn, rượu Vodka Putinka ẩn chứa trong n&amp;oacute; một bầu kh&amp;ocirc;ng kh&amp;iacute; thanh thản v&amp;agrave; sự tin tưởng.&lt;br /&gt; - Juri Dolgoruki: Đ&amp;acirc;y l&amp;agrave; t&amp;ecirc;n vị Sa ho&amp;agrave;ng lừng lẫy trong lịch sử nước Nga, người đ&amp;atilde; th&amp;agrave;nh lập n&amp;ecirc;n thủ đ&amp;ocirc; Mosscow ng&amp;agrave;y nay v&amp;agrave; dựng l&amp;ecirc;n bức tường ki&amp;ecirc;n cố của ph&amp;aacute;o đ&amp;agrave;i Kremlin. Ch&amp;iacute;nh v&amp;igrave; vậy n&amp;ecirc;n d&amp;ograve;ng Vodka Juri Dolgoruki mang trong m&amp;igrave;nh một sắc th&amp;aacute;i ho&amp;agrave;n to&amp;agrave;n kh&amp;aacute;c với những d&amp;ograve;ng Vodka kh&amp;aacute;c.&lt;br /&gt; - Crisstallnaya : C&amp;oacute; độ trong suốt như pha l&amp;ecirc;, quy tr&amp;igrave;nh l&amp;agrave;m rượu rất phức tạp tr&amp;ecirc;n cơ sở sử dụng cồn hảo hạng &amp;ldquo;Lux&amp;rdquo; v&amp;agrave; nước tinh lọc nhiều lần được bổ sung một số phụ gia thực phẩm v&amp;agrave; kho&amp;aacute;ng vật đặc th&amp;ugrave; đ&amp;atilde; tạo cho Vodka Cristallnaya một hương vị tinh tu&amp;yacute; tao nh&amp;atilde;.&lt;br /&gt; - Prozachnaya: Rượu Vodka chuy&amp;ecirc;n d&amp;ugrave;ng cho ng&amp;agrave;y lễ hội. Sự ho&amp;agrave; hợp giữa b&amp;atilde; rượu &amp;ldquo;Extra&amp;rdquo;, nước tinh khiết được tinh chế đặc biệt v&amp;agrave; đường xirup đ&amp;atilde; tạo n&amp;ecirc;n một m&amp;ugrave;i vị đậm đ&amp;agrave;, &amp;ecirc;m dịu, ấm c&amp;uacute;ng rất ph&amp;ugrave; hợp với những ng&amp;agrave;y lễ hội hay những bữa tiệc gia đ&amp;igrave;nh.&lt;br /&gt; - Gzhelka: Thường được những người s&amp;agrave;nh uống coi l&amp;agrave; d&amp;ograve;ng Vodka d&amp;acirc;n d&amp;atilde;, đậm đ&amp;agrave; bản sắc nước Nga. Vodka Gzhelka được xếp v&amp;agrave;o h&amp;agrave;ng những d&amp;ograve;ng Vodka chất lượng cao. Được sản xuất dựa tr&amp;ecirc;n d&amp;acirc;y chuyền c&amp;ocirc;ng nghệ l&amp;acirc;u đời của c&amp;aacute;c d&amp;ograve;ng Vodka ở Nga. Vẫn c&amp;ugrave;ng một c&amp;aacute;ch chế biến độc đ&amp;aacute;o của Nga.&lt;/p&gt;', '4e4f4b80a3f5fMoskovskaya.jpg', 1313814281, 'Các loại Vodka chính', 'Các loại Vodka chính', 'Các loại Vodka chính'),
(15, 2, 'Có phải Bordeaux chỉ nổi tiếng về rượu vang đỏ không?', 'co_phai_bordeaux_chi_noi_tieng_ve_ruou_vang_do_khong', 'Thường khi nhắc tới rượu Bordeaux, ai cũng nhắc tới rượu vang đỏ với hương vị tuyệt hảo. Thế nhưng có một sự thật là vào giai đoạn xa xưa thì Bordeaux nổi tiếng về rượu vang trắng chứ không phải vang đỏ. Hơn 80% diện tích đất ở Bordeaux dùng để sản xuất ra Sauternes, Barsac, Bordeaux Blanc, và Graves. Chỉ tới những năm 70 của thế kỷ XX, khi mà các yếu tố thương mại và chính trị (chia cắt những vùng làm rượu ra) thì rượu vang ở Bordeaux mới đổi mầu từ trắng sang đỏ.', '&lt;p style=&quot;margin: 0px 0px 1.571em; padding: 0px;&quot;&gt;Thường khi nhắc tới &lt;a href=&quot;http://muabanruoubia.com/&quot; title=&quot;rượu&quot;&gt;rượu&lt;/a&gt; Bordeaux, ai cũng nhắc tới &lt;a href=&quot;http://muabanruoubia.com/cat/ruou-ngoai/ruou_vang/&quot; title=&quot;rượu vang&quot;&gt;rượu vang&lt;/a&gt; đỏ với hương vị tuyệt hảo. Thế nhưng c&amp;oacute; một sự thật l&amp;agrave; v&amp;agrave;o giai đoạn xa xưa th&amp;igrave; Bordeaux nổi tiếng về &lt;a href=&quot;http://muabanruoubia.com/2010/ruou-vang-trang/&quot; title=&quot;rượu vang trắng&quot;&gt;rượu vang trắng&lt;/a&gt; chứ kh&amp;ocirc;ng phải vang đỏ. Hơn 80% diện t&amp;iacute;ch đất ở Bordeaux d&amp;ugrave;ng để sản xuất ra Sauternes, Barsac, Bordeaux Blanc, v&amp;agrave; Graves. Chỉ tới những năm 70 của thế kỷ XX, khi m&amp;agrave; c&amp;aacute;c yếu tố thương mại v&amp;agrave; ch&amp;iacute;nh trị (chia cắt những v&amp;ugrave;ng l&amp;agrave;m rượu ra) th&amp;igrave; rượu vang ở Bordeaux mới đổi mầu từ trắng sang đỏ.&lt;/p&gt;\n&lt;p style=&quot;margin: 0px 0px 1.571em; padding: 0px;&quot;&gt;Ng&amp;agrave;y nay, c&amp;oacute; tới 85% diện t&amp;iacute;ch đất ở Bordeaux được sử dụng để sản xuất rượu vang đỏ v&amp;agrave; c&amp;ograve;n c&amp;oacute; rất &amp;iacute;t người biết tới rượu vang trắng ở Bordeaux.&lt;a href=&quot;http://muabanruoubia.com/wp-content/uploads/2010/05/295563459_464dcc10c11.jpg&quot;&gt;&lt;img class=&quot;aligncenter size-full wp-image-821&quot; src=&quot;http://muabanruoubia.com/wp-content/uploads/2010/05/295563459_464dcc10c11.jpg&quot; border=&quot;0&quot; alt=&quot;295563459_464dcc10c1&quot; width=&quot;500&quot; height=&quot;351&quot; /&gt;&lt;/a&gt;&lt;/p&gt;', '4e4f4b9ff28c2295563459_464dcc10c11.jpg', 1313814356, 'Có phải Bordeaux chỉ nổi tiếng về rượu vang đỏ không?', 'Có phải Bordeaux chỉ nổi tiếng về rượu vang đỏ không?', 'Có phải Bordeaux chỉ nổi tiếng về rượu vang đỏ không?');
INSERT INTO `news_contents` (`id`, `news_category_id`, `name`, `friendly_url`, `intro_text`, `content`, `image_path`, `date_created`, `page_title`, `meta_keywords`, `meta_description`) VALUES
(16, 2, 'Mùi hương ẩn giấu của rượu vang', 'mui_huong_an_giau_cua_ruou_vang', 'Cảm nhận mùi là một bước quan trọng để thưởng thức và đánh giá rượu. Có bao giờ bạn tự hỏi thực ra rượu vang có hương vị như thế nào? Liệu nó có giống như những gì mình cảm nhận hay không? Hi vọng một vài chia sẻ dưới đây sẽ giúp bạn thêm hiểu về thức uống nổi tiếng này.', '&lt;div class=&quot;entry clearfix&quot;&gt;\n&lt;div class=&quot;content clearfix&quot;&gt;\n&lt;p&gt;&lt;a href=&quot;http://muabanruoubia.com/wp-content/uploads/2010/04/huong-ruou-vang1.jpg&quot;&gt;&lt;img class=&quot;aligncenter size-full wp-image-564&quot; src=&quot;http://muabanruoubia.com/wp-content/uploads/2010/04/huong-ruou-vang1.jpg&quot; border=&quot;0&quot; alt=&quot;huong-ruou-vang1&quot; width=&quot;500&quot; height=&quot;661&quot; /&gt;&lt;/a&gt;&lt;span style=&quot;color: #1e1e1e; font-family: Arial,''Segoe UI'',Helvetica,sans-serif; font-size: 12px; line-height: 18px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;\n&lt;p style=&quot;border-width: 0px; margin: 15px 0px; padding: 0px; font-size: 12px; background-color: transparent; text-align: justify;&quot;&gt;&lt;strong&gt;Cảm nhận m&amp;ugrave;i l&amp;agrave; một bước quan trọng để thưởng thức v&amp;agrave; đ&amp;aacute;nh gi&amp;aacute; &lt;a href=&quot;http://muabanruoubia.com/&quot; title=&quot;rượu&quot;&gt;rượu&lt;/a&gt;. C&amp;oacute; bao giờ bạn tự hỏi thực ra &lt;a href=&quot;http://muabanruoubia.com/cat/ruou-ngoai/ruou_vang/&quot; title=&quot;rượu vang&quot;&gt;rượu vang&lt;/a&gt; c&amp;oacute; hương vị như thế n&amp;agrave;o? Liệu n&amp;oacute; c&amp;oacute; giống như những g&amp;igrave; m&amp;igrave;nh cảm nhận hay kh&amp;ocirc;ng? Hi vọng một v&amp;agrave;i chia sẻ dưới đ&amp;acirc;y sẽ gi&amp;uacute;p bạn th&amp;ecirc;m hiểu về thức uống nổi tiếng n&amp;agrave;y.&lt;/strong&gt;&lt;/p&gt;\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\n&lt;p style=&quot;border-width: 0px; margin: 15px 0px; padding: 0px; font-size: 12px; background-color: transparent; text-align: justify;&quot;&gt;&lt;em&gt;&lt;strong&gt;Đ&amp;acirc;u l&amp;agrave; m&amp;ugrave;i hương thật của rượu vang?&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;\n&lt;p style=&quot;border-width: 0px; margin: 15px 0px; padding: 0px; font-size: 12px; background-color: transparent; text-align: justify;&quot;&gt;&lt;span style=&quot;border-width: 0px; margin: 0px; padding: 0px; font-size: 12px; background-color: transparent; text-decoration: underline;&quot;&gt;Theo nhiều chuy&amp;ecirc;n gia về rượu, m&amp;ugrave;i vị của rượu vang thường được chia l&amp;agrave;m&lt;span&gt; &lt;/span&gt;&lt;strong&gt;5 nh&amp;oacute;m&lt;/strong&gt;&lt;span&gt; &lt;/span&gt;cơ bản sau đ&amp;acirc;y:&lt;/span&gt;&lt;/p&gt;\n&lt;ol style=&quot;border-width: 0px; margin: 15px 0px; padding: 0px; font-size: 12px; background-color: transparent;&quot;&gt;\n&lt;li&gt;M&amp;ugrave;i hoa quả: M&amp;ugrave;i thơm nhẹ của t&amp;aacute;o, đ&amp;agrave;o, d&amp;acirc;u t&amp;acirc;y; c&amp;aacute;c loại tr&amp;aacute;i c&amp;acirc;y ch&amp;iacute;n mọng đậm m&amp;agrave;u như mận, cherry; xo&amp;agrave;i, dưa hấu của miền nhiệt đới hay c&amp;aacute;c loại quả họ cam như chanh, cam v&amp;agrave; nho.&lt;/li&gt;\n&lt;li&gt;M&amp;ugrave;i thảo mộc: Cỏ xạ hương v&amp;agrave; oải hương l&amp;agrave; hai m&amp;ugrave;i thường thấy ở nhiều loại rượu vang. Một v&amp;agrave;i loại vang trắng lại mang m&amp;ugrave;i thơm m&amp;aacute;t của những loại cỏ dại.&lt;/li&gt;\n&lt;li&gt;Hương hoa: Đ&amp;uacute;ng l&amp;agrave; &lt;a href=&quot;http://muabanruoubia.com/2010/cac-loai-ruou-vang/&quot; title=&quot;c&amp;aacute;c loại rượu vang&quot;&gt;c&amp;aacute;c loại rượu vang&lt;/a&gt; thường rất &amp;iacute;t khi c&amp;oacute; được m&amp;ugrave;i thơm của hoa, m&amp;agrave; đặc biệt l&amp;agrave; hoa hồng. Nhưng đ&amp;ocirc;i khi hương hoa vẫn xuất hiện trong một v&amp;agrave;i loại vang đặc biệt.&lt;/li&gt;\n&lt;li&gt;M&amp;ugrave;i đất, m&amp;ugrave;i da v&amp;agrave; cả m&amp;ugrave;i&amp;hellip;. thuốc l&amp;aacute;: M&amp;ugrave;i nồng đượm của đất được coi l&amp;agrave; m&amp;ugrave;i hương kh&amp;aacute; dễ chịu với rượu vang. M&amp;ugrave;i da hay thuốc l&amp;aacute; thường c&amp;oacute; nhiều nhất ở c&amp;aacute;c loại vang đỏ.&lt;/li&gt;\n&lt;li&gt;M&amp;ugrave;i gỗ v&amp;agrave; vani: Chỉ c&amp;oacute; những loại rượu được bảo quản trong gỗ sồi c&amp;oacute; được m&amp;ugrave;i thơm đặc biệt của gỗ. Gỗ sồi cũng ch&amp;iacute;nh l&amp;agrave; nguy&amp;ecirc;n liệu để tạo ra hương vani ngọt dịu nh&amp;acirc;n tạo trong rượu.&lt;/li&gt;\n&lt;/ol&gt;\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\n&lt;p style=&quot;border-width: 0px; margin: 15px 0px; padding: 0px; font-size: 12px; background-color: transparent; text-align: justify;&quot;&gt;&lt;em&gt;&lt;strong&gt;C&amp;oacute; c&amp;aacute;ch n&amp;agrave;o để nhận ra m&amp;ugrave;i hương thật n&amp;agrave;y?&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;\n&lt;ol style=&quot;border-width: 0px; margin: 15px 0px; padding: 0px; font-size: 12px; background-color: transparent;&quot;&gt;\n&lt;li&gt;Kh&amp;ocirc;ng phải ngẫu nhi&amp;ecirc;n m&amp;agrave; những loại ly ph&amp;igrave;nh to ở bụng v&amp;agrave; thon v&amp;agrave;o ở miệng thường &amp;ldquo;song h&amp;agrave;nh&amp;rdquo; c&amp;ugrave;ng rượu tr&amp;ecirc;n mỗi b&amp;agrave;n tiệc. Người ta cho rằng loại ly n&amp;agrave;y được sinh ra l&amp;agrave; d&amp;agrave;nh cho rượu, v&amp;igrave; thiết kế n&amp;agrave;y l&amp;agrave;m tăng hương vị của rượu v&amp;agrave; gi&amp;uacute;p cho những người thưởng thức c&amp;oacute; thể dễ d&amp;agrave;ng cảm nhận được m&amp;ugrave;i hương của n&amp;oacute;.&lt;/li&gt;\n&lt;li&gt;Ta vẫn thường lắc cốc trước khi thưởng thức m&amp;ugrave;i hương v&amp;agrave; vị nồng đượm của vang, bởi đ&amp;acirc;y l&amp;agrave; c&amp;aacute;ch hiệu quả để k&amp;iacute;ch th&amp;iacute;ch m&amp;ugrave;i thơm của rượu bay hơi nhiều hơn. Tuy vậy, hương vị thật ẩn b&amp;ecirc;n trong c&amp;oacute; thể bị lấn &amp;aacute;t bởi những m&amp;ugrave;i vượt trội m&amp;agrave; ta c&amp;oacute; thể ngay lập tức &amp;ldquo;bắt được&amp;rdquo; sau khi lắc cốc. Tiếp x&amp;uacute;c với hương rượu vang trước khi lắc cốc l&amp;agrave; một trong những c&amp;aacute;ch kh&amp;ocirc;ng tồi để kh&amp;aacute;m ph&amp;aacute; những độc đ&amp;aacute;o của vang.&lt;/li&gt;\n&lt;li&gt;Mũi c&amp;agrave;ng gần rượu th&amp;igrave; lại c&amp;agrave;ng nhanh nhận ra m&amp;ugrave;i của ch&amp;uacute;ng. Nhưng chỉ lướt qua ly rượu trong khoảng v&amp;agrave;i gi&amp;acirc;y xem ra lại l&amp;agrave; c&amp;aacute;ch tốt nhất nếu muốn cảm nhận m&amp;ugrave;i hương thật ẩn giấu b&amp;ecirc;n trong tất cả hương vị nồng n&amp;agrave;n dễ thấy. Bởi mũi sẽ nhanh ch&amp;oacute;ng bị &amp;ldquo;chai sạn&amp;rdquo; với m&amp;ugrave;i hương trong 6 gi&amp;acirc;y. Ngửi thật l&amp;acirc;u quả kh&amp;ocirc;ng phải l&amp;agrave; c&amp;aacute;ch hiệu quả để cố gắng t&amp;igrave;m kiếm hương vị tinh tế của rượu.&lt;/li&gt;\n&lt;li&gt;Khi nhấm nh&amp;aacute;p rượu, c&amp;oacute; thể bạn sẽ cảm thấy ch&amp;uacute;ng kh&amp;ocirc;ng như những g&amp;igrave; m&amp;agrave; bạn đ&amp;atilde; ngửi. Nhiệt độ thay đổi cũng l&amp;agrave; l&amp;uacute;c rượu bắt đầu c&amp;oacute; m&amp;ugrave;i hương mới. Trong trường hợp n&amp;agrave;y, sức n&amp;oacute;ng ấm của khoang miệng v&amp;agrave; nước bọt ch&amp;iacute;nh l&amp;agrave; nh&amp;acirc;n tố khiến cho rượu phần n&amp;agrave;o biến đổi hương vị của m&amp;igrave;nh.&lt;/li&gt;\n&lt;/ol&gt;\n&lt;p style=&quot;border-width: 0px; margin: 15px 0px; padding: 0px; font-size: 12px; background-color: transparent; text-align: center;&quot;&gt;&lt;img src=&quot;http://monngonhanoi.com/images/stories/2010/03/huong-ruou-vang3.jpg&quot; border=&quot;0&quot; alt=&quot;&quot; width=&quot;500&quot; height=&quot;461&quot; /&gt;&lt;/p&gt;\n&lt;/div&gt;\n&lt;/div&gt;', '4e4f4bc384b2fhuong-ruou-vang1.jpg', 1313814422, 'Mùi hương ẩn giấu của rượu vang', 'Mùi hương ẩn giấu của rượu vang', 'Mùi hương ẩn giấu của rượu vang');

